import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

import Header from "./common/Header";
import Footer from "./common/Footer";

import ScriptTag from "react-script-tag";
import AOS from "aos";

export default class Honourary extends Component {
  constructor(props) {
    super(props);
    AOS.init();
    this.state = {
      members: [],
      currentPage: 1,
      membersPerPage: 20,
      query: "",
      isLoading: true,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentWillReceiveProps() {
    AOS.refresh();
  }

  handleClick(event) {
    event.preventDefault();
    this.setState({
      currentPage: Number(event.target.id),
    });
  }

  componentDidMount() {
    this.fetchMembers();
  }

  fetchMembers = (e) => {
    axios
      .get(`${axios.defaults.baseURL}/list-honourary`)
      .then((response) => {
        // console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ members: json.data.honourary, isLoading: false });
        } else alert("Fetching Honourary Members Failed!");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  };

  render() {const { members, currentPage, membersPerPage, isLoading } = this.state;

  // Logic for displaying members
  const membersList = this.state.members;
  const indexOfLastMember = currentPage * membersPerPage;
  const indexOfFirstMember = indexOfLastMember - membersPerPage;
  const currentMembers = membersList.slice(indexOfFirstMember, indexOfLastMember);

  const renderMembers = currentMembers.map((member, index) => {
    return (
      <div
        className="honourary-card col"
        key={member.id}
        data-aos="fade-up"
                    data-aos-offset="300"
                    data-aos-easing="ease-in-sine"
                    data-aos-duration="1000"
      >
          <div className="honourary-left">
          <img src={member.image} alt={member.image} className="honourary-img" />
          </div>
          <div className="honourary-right">
          <p className="honourary-name typography-section-intro">{member.name}</p>
          <p className="honourary-about">{member.about}</p>
          </div>

          </div>
      );
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(members.length / membersPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map((number) => {
      return (
        <>
          {number == currentPage ? (
            <li
              className="pg-link active-link"
              key={number}
              id={number}
              onClick={this.handleClick}
            >
              {number}
            </li>
          ) : (
            <li
              className="pg-link"
              key={number}
              id={number}
              onClick={this.handleClick}
            >
              {number}
            </li>
          )}
        </>
      );
    });

    return (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <section
          id="section-quality"
          className="section section-quality"
          data-anim-scroll-group="section-quality"
          data-analytics-fallback-section-engagement="name:audio-quality"
        >
          <div
            className="copy-scroll-container"
            data-component-list="CopyMagic"
          >
            <div className="sticky-copy-lockup flex-copy-lockup intro">
              <div
                className="copy-block align-text"
                data-copy="0"
                data-copy-small="0"
                data-copy-fade-at-small="75"
                data-time-offset="75"
              >
                <h3
                  className="typography-section-headline"
                  data-aos="zoom-out-left"
                  data-aos-offset="100"
                  data-aos-easing="ease-in-sine"
                  data-aos-duration="500"
                >
                  Honourary Members
                </h3>
                <p className="typography-section-eyebrow default-text">
                Intelligent minds that have contributed immensely to the society.
                </p>
                <div style={{marginTop:"50px"}}>
                    {renderMembers}
                </div>
                {/* <div className="align-center">{renderPageNumbers}</div> */}
            </div>
            </div>
          </div>
        </section>

        <section
          id="section-ar"
          className="section section-ar theme-dark"
          data-anim-scroll-group="section-ar"
          data-analytics-fallback-section-engagement="name:ar"
          data-analytics-section-custom="ar"
          data-analytics-section-engagement="name:ar"
        >
          <div className="section-content">
            <div className="banner-copy">
              <div className="icon-ar"></div>
              <h3
                className="typography-router-headline"
                data-aos="slide-left"
                data-aos-offset="100"
                data-aos-easing="ease-in-sine"
                data-aos-duration="500"
              >
                Become a<br />
                Member!
              </h3>
              <p
                className="typography-router-tagline ar-copy hide-on-quicklook"
                data-aos="fade-in"
                data-aos-offset="200"
                data-aos-easing="ease-in-sine"
                data-aos-duration="1000"
              >
                Join the community of Intellectuals.
              </p>
              <Link
                to="/register"
                className="ac-ln-button ar-copy hide-on-quicklook"
                data-aos="fade-in"
                data-aos-offset="200"
                data-aos-easing="ease-in-sine"
                data-aos-duration="1000"
              >
                Join Now
              </Link>
            </div>
            <figure
              className="image-ar"
              data-aos="slide-up"
              data-aos-offset="100"
              data-aos-easing="ease-in-sine"
              data-aos-duration="500"
            ></figure>
          </div>
        </section>
        {/*/main*/}
        <Footer />
        {/* Template Main JS File */}
        <ScriptTag
          type="text/javascript"
          src="../assets/js/built.js"
        />
      </body>
    );
  }
}
