import React, { Component } from "react";
import { Link } from "react-router-dom";

import axios from "axios";
import Avatar from "../assets/img/default-user-avatar.png";
import Logo from "../assets/img/favicon.png";

import swal from "sweetalert";
import $ from "jquery";

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      isLoggedIn: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).isLoggedIn
        : "",
    };
  }
  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ user: json.data.user[0] });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }
  logoutUser = () => {
    localStorage.clear();
    window.location.href = "/login";
  };

  render() {
    return (
      <>

      </>
    );
  }
}
