import React from "react";
import { render } from "react-dom";
import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";
import axios from "axios";

import Home from "./Home";
import Gallery from "./Gallery";
import Stories from "./Stories";

import "./assets/css/style.css";
import Funeral from "./Funeral";
import Tribute from "./Tribute";
import Life from "./Life";


// Define App URL
axios.defaults.baseURL = 'https://api.adeniyiogunwuyi.com/api/v1';
// axios.defaults.baseURL = 'http://localhost:8000/api/v1';

class App extends React.Component {
  constructor(props) {
  super(props);
    this.state = {
      play: false,
      pause: true,
    };
    this.url = "https://adeniyiogunwuyi.com/puff.mp3";
    this.audio = new Audio(this.url);
  }

  componentDidMount(){
      this.audio.play();
  }
  render() {
    return (
          <Switch data="data">
            <div id="main">
                  <Route exact path="/" component={Home}/>
                  {/* <Route exact path="gallery" component={Gallery}/>
                  <Route exact path="stories" component={Stories}/> */}
                  <Route exact path="/life" render={props => (<Life {...props}/>)}/>
                  <Route exact path="/tributes" render={props => (<Tribute {...props}/>)}/>
                  <Route exact path="/gallery" render={props => (<Gallery {...props}/>)}/>
                  <Route exact path="/stories" render={props => (<Stories {...props}/>)}/>
                  <Route exact path="/funeral-arrangements" render={props => (<Funeral {...props}/>)}/>
            </div>
          </Switch>
    );
  }
}

const AppContainer = withRouter(props => <App {...props} />);
// console.log(store.getState())
render(
  <BrowserRouter>
    <AppContainer />
  </BrowserRouter>,

  document.getElementById("root")
);
