import React, { Component } from "react";
import { Link, Redirect, Route } from "react-router-dom";

import Header from "./common/Header";
import Footer from "./common/Footer";

import ScriptTag from "react-script-tag";
import NumberFormat from "react-number-format";
import { PaystackConsumer } from "react-paystack";

import AOS from "aos";
import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

import Avatar from "./assets/img/default-user-avatar.png";

var _fname, _lname, _phone, _mstatus, _address, _dob, _password;

export class Profile extends Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      imageSource: "",
      file: null,
      payment_ref: "",
      auth_code: "",
    };
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {
    // //console.log(id);
    this.getUser();
  }

  getUser() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ user: json.data.user[0] });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  handleUpdate = (e) => {
    e.preventDefault();

    $("#update-profile")
      .attr("disabled", "disabled")
      .html(
        '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Loading...</span>'
      );

    var formData = new FormData();
    formData.append("fname", _fname.value);
    formData.append("lname", _lname.value);
    formData.append("phone", _phone.value);
    formData.append("image", this.state.file);
    formData.append("password", _password.value);
    formData.append("mstatus", _mstatus.value);
    formData.append("address", _address.value);
    formData.append("dob", _dob.value);

    //console.log(formData);

    axios({
      method: "post",
      url: `${axios.defaults.baseURL}/user/${
        JSON.parse(localStorage["appState"]).user.id
      }`,
      data: formData,
      headers: {
        Authorization: `Bearer ${this.state.token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        const {
          id,
          fname,
          lname,
          email,
          phone,
          mstatus,
          address,
          dob,
        } = json.data.user;
        const { token } = json.data.token;
        let userData = {
          id,
          fname,
          lname,
          email,
          phone,
          mstatus,
          address,
          dob,
          token,
          timestamp: new Date().toString(),
        };
        let appState = {
          isLoggedIn: true,
          user: userData,
        };
        // save app state with user date in local storage
        localStorage["appState"] = JSON.stringify(appState);
        this.setState({
          isLoggedIn: appState.isLoggedIn,
          user: appState.user,
          token: appState.token,
        });

        if (json.data.success === true) {

          this.getUser();

          swal("Updated!", "" + json.data.message, "success", {
            button: false,
            timer: 1500,
          });

          $("#update-profile").removeAttr("disabled").html("Update Profile");
          // redirect home
          // this.props.history.push("/my-account");
        } else {
          // alert(json.data.message);
          swal("Alert!", "" + json.data.message, "warning");
          $("#update-profile").removeAttr("disabled").html("Update Profile");
        }
      })
      .catch((error) => {
        // alert("An Error Occured!" + error);
        //console.log(error.response.data);
        if (error.response.data.message.fname === "") {
          swal("Warning!", "" + error.response.data.message.fname, "warning");
        } else {
          swal("Warning!", "Please fill all fields", "warning");
        }
        $("#update-profile").removeAttr("disabled").html("Update Profile");
      });
  };

  // onChange(e) {
  //   this.setState({file:e.target.files[0]});
  // }

  uploadPhoto = (e) => {
    this.photoUpload.click();
  };

  onChange = (event) => {
    this.setState({
      imageSource: "",
      imageData: "",
      file: event.target.files[0],
    });
    const file = event.target.files[0];
    //console.log(file);

    if (file) {
      let reader = new FileReader();
      reader.readAsDataURL(event.target.files[0]);
      reader.onload = (event) => {
        this.setState({
          imageSource: event.target.result,
          imageData: file,
        });
      };
    }
  };

  render() {
    $("#fname-input").attr("value", this.state.user.fname);
    $("#lname-input").attr("value", this.state.user.lname);
    $("#email-input").attr("value", this.state.user.email);
    $("#phone-input").attr("value", this.state.user.phone);
    $("#mstatus-input").attr("value", this.state.user.mstatus);
    $("#address-input").attr("value", this.state.user.address);
    $("#dob-input").attr("value", this.state.user.dob);

    const configNGN = {
      // reference: (new Date()).getTime(),
      email: this.state.user.email,
      // plan: this.state.plan,
      amount: "10000",
      currency: "NGN",
      publicKey: "pk_live_68d2b6e9edacd11392f5548db9047ffb4911b39d",
      // publicKey: "pk_test_3a1c9194d014f2629abda6e0c3f22cb931141cf4",
    };

    const componentPropsNGN = {
      ...configNGN,
      text: "Bank Card Update",
      onSuccess: (response) => {
        // console.log(response);

        this.setState({
          payment_ref: response.trxref,
        });

        axios({
          method: "get",
          url: `https://api.paystack.co/transaction/verify/${this.state.payment_ref}`,
          headers: {
            Authorization: `Bearer sk_live_5299c2d2e25663425657490251d1cec7d8c206aa`,
            "Content-Type": "application/json",
          },
          // headers: {'Authorization': `Bearer sk_test_2e3a16e2c572cbcfd59cbc761105f58b7a34e90d`, 'Content-Type': 'application/json' }
        })
          .then((response) => {
            //console.log(response);
            return response;
          })
          .then((json) => {
            this.setState({
              auth_code: json.data.data.authorization.authorization_code,
            });

            swal("Success!", "Card Update was Successful!", "success", {
              button: false,
              timer: 1500,
            });

            var formData = new FormData();
            formData.append("auth_code", this.state.auth_code);
            formData.append(
              "user_id",
              JSON.parse(localStorage["appState"]).user.id
            );

            axios({
              method: "post",
              url: `${axios.defaults.baseURL}/update-user-card`,
              data: formData,
              headers: {
                Authorization: `Bearer ${
                  JSON.parse(localStorage["appState"]).user.token
                }`,
                "Content-Type": "multipart/form-data",
              },
            })
              .then((response) => {
                //console.log(response);
                return response;
              })
              .then((json) => {
                if (json.data.success) {
                  // swal("Success!", "" + json.data.message, "success", {
                  //   button: false,
                  //   timer: 1500,
                  // });
                  // redirect to plans
                  this.props.history.push("/my-account");
                } else {
                  swal("Failed", "" + json.data.message, "error");
                }
              })
              .catch((error) => {
                // alert("An Error Occured!" + error);
                let res = "An Error Occured";
                swal("Error!", "" + res, "error", {
                  button: false,
                  // timer: 1500
                });
                //console.log(`${formData} ${error}`);
              });
          });
      },
      onClose: () => null,
    };

    return (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <section
          id="section-battery"
          className="section section-battery theme-dark"
          data-anim-scroll-group="section-battery"
          data-analytics-fallback-section-engagement="name:battery"
        >
          <div
            className="copy-scroll-container"
            data-component-list="CopyMagic"
          >
            <div className="sticky-copy-lockup flex-copy-lockup intro">
              <div className="" data-copy="0">
                <p className="typography-section-eyebrow align-text default-text">
                  Your Sapio Profile!
                </p>
                <div className="avatar-center">
                  <img
                    src={
                      this.state.imageSource
                        ? this.state.imageSource
                        : this.state.user.image
                        ? this.state.user.image
                        : Avatar
                    }
                    id="user-logo"
                    className="user-avatar"
                  />
                  <p
                    role="button"
                    onClick={this.uploadPhoto}
                    style={{
                      cursor: "pointer",
                    }}
                  >
                    Change Photo
                  </p>
                </div>
                <br />
                <form
                  id="reg"
                  action=""
                  onSubmit={this.handleUpdate}
                  method="post"
                >
                  <input
                    ref={(input) => (this.photoUpload = input)}
                    onChange={this.onChange}
                    autoComplete="off"
                    id="image-input"
                    name="image"
                    type="file"
                    hidden
                  />
                  <div className="row">
                    <div className="form-group col">
                      <label for="firstname" className="sr-only">
                        Firstname
                      </label>
                      <br />
                      <input
                        ref={(input) => (_fname = input)}
                        type="firstname"
                        name="firstname"
                        id="fname-input"
                        className="form-control input-field"
                        placeholder="Firstname"
                      />
                    </div>
                    <div className="form-group col">
                      <label for="lastname" className="sr-only">
                        Lastname
                      </label>
                      <br />
                      <input
                        ref={(input) => (_lname = input)}
                        type="lastname"
                        name="lastname"
                        id="lname-input"
                        className="form-control input-field"
                        placeholder="Lastname"
                      />
                    </div>
                  </div>
                  <div className="row">
                    <div className="form-group col">
                      <label for="address" className="sr-only">
                        Address
                      </label>
                      <br />
                      <input
                        ref={(input) => (_address = input)}
                        type="address"
                        name="address"
                        id="address-input"
                        className="form-control input-field"
                        placeholder="Address"
                      />
                    </div>
                    <div className="form-group col">
                      <label for="telephone" className="sr-only">
                        Telephone
                      </label>
                      <br />
                      <input
                        ref={(input) => (_phone = input)}
                        type="telephone"
                        name="telephone"
                        id="phone-input"
                        className="form-control input-field"
                        placeholder="Telephone"
                      />
