import React, { Component } from "react";
import { Link } from "react-router-dom";

export class MenuList extends Component {
  render() {
    let location = window.location.href.split("/")[
      window.location.href.split("/").length - 1
    ];

    //console.log(location);

    const dashboardClass =
      location === "dashboard"
        ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary"
        : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";

    const refClass =
      location === "referrals"
        ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary"
        : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";

    const accountClass =
      location === "my-account"
        ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary"
        : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";

    return (
      <div>
        <ul className="list-unstyled">
          <li>
            <Link to="/dashboard" className={dashboardClass}>
              <i className="mr-1 ti-dashboard"></i> Dashboard
            </Link>
          </li>
          <li>
            <Link to="/referrals" className={refClass}>
              <i className="mr-1 ti-world"></i> Referrals
            </Link>
          </li>
          <li>
            <Link to="/my-account" className={accountClass}>
              <i className="mr-1 ti-user"></i> My Account
            </Link>
          </li>
        </ul>
      </div>
    );
  }
}

export default MenuList;
