import React from "react";
import { Link } from "react-router-dom";

import Logo from '../assets/img/logo-light.svg'
import Image from '../assets/img/login.png'

const Login = ({ history, loginUser = f => f }) => {
  let _email, _password;
  const handleLogin = e => {
    e.preventDefault();
    loginUser(_email.value, _password.value);
  };
  return (
    <main className="login-body d-flex align-items-center min-vh-100 py-3 py-md-0">
                <div className="container">
                <div className="card login-card">
                    <div className="row no-gutters">
                    <div className="col-md-7">
                        <img src={Image} alt="login" className="login-card-img"/>
                    </div>
                    <div className="col-md-5">
                        <div className="card-body">
                        <div className="brand-wrapper">
                            <img src={Logo} alt="logo" className="logo"/>
                        </div>
                        <p className="login-card-description">Sign into your account</p>
                        <form action="#!">
                            <div className="form-group">
                                <label for="email" className="sr-only">Email</label>
                                <input type="email" name="email" id="email" className="form-control" placeholder="Email address"/>
                            </div>
                            <div className="form-group mb-4">
                                <label for="password" className="sr-only">Password</label>
                                <input type="password" name="password" id="password" className="form-control" placeholder="***********"/>
                            </div>
                            <input name="login" id="login" className="btn btn-block login-btn mb-4" type="button" value="Login"/>
                            </form>
                            <a href="#!" className="forgot-password-link">Forgot password?</a>
                            <p className="login-card-footer-text">Don't have an account? <Link to="/register" className="text-reset">Register</Link></p>
                            <nav className="login-card-footer-nav">
                            <a href="#!">Privacy policy</a>
                            </nav>
                        </div>
                    </div>
                    </div>
                    </div>
                </div>
                </main>
  );
};
export default Login;
