import React from "react";
import { Link } from "react-router-dom";

const Register = ({ history, registerUser = f => f }) => {
  let _fname, _lname, _email, _phone, _password;

  const handleLogin = e => {
    e.preventDefault();

    registerUser(_fname.value, _lname.value, _email.value, _phone.value, _password.value);
  };
  return (
    <div id="main">
      <form id="login-form" action="" onSubmit={handleLogin} method="post">
        <h3 style={{ padding: 15 }}>Register Form</h3>
        <input
          ref={input => (_fname = input)}
          style={styles.input}
          autoComplete="off"
          id="fname-input"
          name="fname"
          type="text"
          className="center-block"
          placeholder="Enter Firstname"
        />
        <input
          ref={input => (_lname = input)}
          style={styles.input}
          autoComplete="off"
          id="lname-input"
          name="lname"
          type="text"
          className="center-block"
          placeholder="Enter Lastname"
        />
        <input
          ref={input => (_email = input)}
          style={styles.input}
          autoComplete="off"
          id="email-input"
          name="email"
          type="text"
          className="center-block"
          placeholder="Enter Email"
        />
        <input
          ref={input => (_phone = input)}
          style={styles.input}
          autoComplete="off"
          id="phone-input"
          name="phone"
          type="text"
          className="center-block"
          placeholder="Enter Telephone"
        />
        <input
          ref={input => (_password = input)}
          style={styles.input}
          autoComplete="off"
          id="password-input"
          name="password"
          type="password"
          className="center-block"
          placeholder="Enter Password"
        />
        <button
          type="submit"
          style={styles.button}
          className="landing-page-btn center-block text-center"
          id="email-login-btn"
          href="#facebook"
        >
          Register
        </button>

        <Link style={styles.link} to="/login">
          Login
        </Link>
      </form>
    </div>
  );
};
const styles = {
  input: {
    backgroundColor: "white",
    border: "1px solid #cccccc",
    padding: 15,
    float: "left",
    clear: "right",
    width: "80%",
    margin: 15
  },
  button: {
    height: 44,
    boxShadow: "0px 8px 15px rgba(0, 0, 0, 0.1)",
    border: "none",
    backgroundColor: "red",
    margin: 15,
    float: "left",
    clear: "both",
    width: "85%",
    color: "white",
    padding: 15
  },
  link: {
    width: "100%",
    float: "left",
    clear: "both",
    textAlign: "center"
  }
};

export default Register;
