import React, { Component } from "react";
import Page from "react-page-loading";
import { Pie, Line, Bar, Chart, Doughnut } from "react-chartjs-2";

import Header from "./common/Header";
import Sidebar from "./common/Sidebar";
import { Link } from "react-router-dom";

import axios from "axios";
import swal from "sweetalert";

export class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      plan_count: "",
      plan_current: "",
      plan_total: "",
    };
  }
  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            user: json.data.user[0],
            plan_count: json.data.plans_count,
            plan_current: json.data.plans_current,
            plan_total: json.data.plans_total,
          });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }
  logoutUser = () => {
    localStorage.clear();
    window.location.href = "/auth";
  };
  render() {
    var r_balance = this.state.plan_total - this.state.plan_current;
    const doudata = {
      labels: ["Payments", "Balance"],
      datasets: [
        {
          data: [this.state.plan_current, r_balance],
          backgroundColor: ["#449400", "#FD5B00"],
        },
      ],
    };

    return (
      <>
        <Header />

        <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <div className="flex-copy-lockup hero-lockup">
              <div className="copy-block">
                <h1
                  className="typography-hero-headline center-align-text"
                  data-headline
                  data-aos="flip-right"
                  data-aos-offset="300"
                  data-aos-easing="ease-in-quart"
                  data-aos-duration="500"
                >
                  SapioClub<sup>®</sup>
                </h1>
                <div className="link-container"></div>
              </div>
            </div>
            <figure
              className="static-image-1"
              data-aos="fade-in"
              data-aos-offset="400"
              data-aos-easing="ease-in-quad"
              data-aos-duration="2000"
            ></figure>
            <div
              className="copy-scroll-container"
              data-component-list="CopyMagic"
            >
              <div className="sticky-copy-lockup flex-copy-lockup">
                <div
                  className="copy-block large-centered padding-bottom-110px small-padding"
                  data-copy="0"
                >
                  <p
                    className="typography-hero-bullets center-align-text"
                    data-aos="fade-up"
                    data-aos-offset="100"
                    data-aos-easing="ease-in-sine"
                    data-aos-duration="500"
                  >
                    The Sapiosexual Elite <br className="large" />
                    <br className="medium" />
                    Association (SEA).
                  </p>
                </div>
                {/* <div className="copy-block large-centered padding-bottom-110px small-padding" data-copy="100" data-copy-small="100">
						<p className="typography-hero-bullets center-align-text">“Empowering minds”</p>
					</div> */}
              </div>
            </div>
            <figure
              className="static-image-2"
              data-aos="fade-up"
              data-aos-offset="100"
              data-aos-easing="ease-in-sine"
              data-aos-delay="5000"
              data-aos-duration="500"
            ></figure>
          </section>
      </>
    );
  }
}

export default Dashboard;
