import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./assets/css/style.css";
import Header from "./partials/Header";
import HBook from "./assets/img/h-post.png"


class Home extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      books: [],
      bookfireice: [],
      query: "",
      bookName:"A game of thrones",
    };
  }

  componentDidMount() {
    // alert(this.state.token);
    this.getBookFireIce();
    this.getBooks();
    
  }


  getBookFireIce(){
    axios
      .get(`${axios.defaults.baseURL}/external-book?name=${this.state.bookName}`
      // .get(`${axios.defaults.baseURL}/external-book?name=`+"A game of thrones"
      )
      .then(response => {
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
        console.log(json.data);
          this.setState({ bookfireice: json.data });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  
  getBookByName = e => {
    e.preventDefault();
    this.setState({
      query: e.target.value
    });
    axios
      .get(`${axios.defaults.baseURL}/external-book?name=${this.state.query}`)
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
          console.log(json.data);
            this.setState({ bookfireice: json.data });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  getBooks(){
    axios
      .get(`${axios.defaults.baseURL}/v1/books`
      )
      .then(response => {
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
        console.log(json.data.books);
          this.setState({ books: json.data.books });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }


  render() {

    return (
      <div>
      <Header/>
      <div className="vertical-book-cards">
        <div className="v-scrolling-wrapper-flexbox">
              <h1>Requirement 1</h1>
              <div className="">
                Enter Name of Book<br/>
              <input name="query" onChange={this.getBookByName} />
            <pre>
              <code>
              {JSON.stringify(this.state.bookfireice, null, 3) }
              </code>
            </pre>
          </div>
        </div>
        <br/>
        <br/>
        <br/>
        <div className="v-scrolling-wrapper-flexbox">
              <h1>Requirement 2</h1>
              <h3>1. Create Book</h3>
              <div className="">
                
                Enter Name of Book<br/>
              <input name="name"/><br/>
                Enter ISBN<br/>
              <input name="isbn"/><br/>
                Enter Authors<br/>
              <input name="authors"/><br/>
                Enter Country<br/>
              <input name="country"/><br/>
                Enter Number of Pages<br/>
              <input name="number_of_pages"/><br/>
                Enter Publisher<br/>
              <input name="publisher"/><br/>
                Enter Rekease Date<br/>
              <input name="release_date"/><br/>
            <pre>
              <code>
              {JSON.stringify(this.state.books, null, 3) }
              </code>
            </pre>
            </div>
            <br/>
            <br/>
            <h3>2. All Books</h3>
              <div className="">
            <pre>
              <code>
              {JSON.stringify(this.state.books, null, 3) }
              </code>
            </pre>
          </div>
        </div>
      </div>
    </div>
    );
  }
}
export default Home;
