import React, { Component } from 'react'
import Light from "../assets/images/light.png"
import { Link } from "react-router-dom";

export default class Header extends Component {
    constructor(props) {
    super(props);
      this.state = {
        play: false,
        pause: true,
      };
      this.url = "https://adeniyiogunwuyi.com/puff.mp3";
      this.audio = new Audio(this.url);
    }

    componentDidMount(){
        // this.audio.play();
    }

    play = () => {
        this.setState({
            play:true, pause: false,
        });
        this.audio.play();
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark theme-bg">
                <Link className="navbar-brand" to="/"><img src={Light} className="light" alt="..."/> Henry Adeniyi Ogunwuyi</Link>
                    {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button> */}
                </nav>
                <div className="menu-wrapper">
          <div className="menu-right">
            <ul>
              <li><Link to="life">Life </Link></li>
              <li><Link to="gallery">Gallery</Link></li>
              <li><Link to="tributes">Tributes</Link></li>
              <li><Link to="stories">Stories </Link></li>
              <li><Link to="funeral-arrangements">Funeral</Link></li>
              <li><Link onClick={this.play}>Play</Link></li>
            </ul>
          </div>
        </div>
            </div>
        )
    }
}
