    formData.append("name", _name.value);
    formData.append("description", _description.value);
    formData.append("image", this.state.file != "" ? this.state.file : this.state.home.image);
    formData.append("price", _price.value);
    formData.append("type", _type.value);
    formData.append("duration", _duration.value);
    formData.append("plan_code", _plan_code.value);

    console.log(formData);

    axios({
      method: "post",
      url: `${axios.defaults.baseURL}/add-home`,
      data: formData,
      headers: {
        Authorization: `Bearer ${this.state.token}`,
        "Content-Type": "multipart/form-data",
      },
    })
      .then((response) => {
        console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success === true) {
          swal("Updated!", "" + json.data.message, "success", {
            button: false,
            timer: 1500,
          });
          $("#update-profile").removeAttr("disabled").html("Update Home");
          // redirect home
          this.props.history.push("/admin/evest-list");
        } else {
          // alert(json.data.message);
          swal("Alert!", "" + json.data.message, "warning");
          $("#update-profile").removeAttr("disabled").html("Update Home");
        }
      })
      .catch((error) => {
