import React from "react";
import { render } from "react-dom";
import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import swal from "sweetalert";
import $ from "jquery";
import PrivateRoute from "./PrivateRoute";

import Home from "./Home";
import Membership from "./Membership";
import About from "./About";
import Event from "./Event";
import Services from "./Services";
import Contact from "./Contact";
import Privacy from "./Privacy";
import Login from "./auth/Login";
import Register from "./auth/Register";

import Dashboard from "./Dashboard";
import Referrals from "./Referrals";
import Profile from "./Profile";

// Admin Pages

import AdminLogin from "./admin/AdminLogin";
import AdminDashboard from "./admin/Dashboard";
import UsersList from "./admin/Users";
import UserDetails from "./admin/UserDetails";
import AdminProfile from "./admin/Profile";

// Define App URL
// axios.defaults.baseURL = 'https://api.sapioclub.com/api/v1';
axios.defaults.baseURL = "http://localhost:8000/api/v1";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      user: {},
    };
  }
  _loginAdmin = (email, password) => {
    $("#login-form button")
      .attr("disabled", "disabled")
      .html(
        '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Logging In...</span>'
      );
    var formData = new FormData();
    formData.append("email", email);
    formData.append("password", password);

    axios
      .post(`${axios.defaults.baseURL}/admin-login`, formData)
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          // alert(json.data.message);
          swal("Success!", json.data.message, "success", {
            button: false,
            timer: 1500,
          });
          const { id, fname, lname, email, phone } = json.data.user;
          const { token } = json.data.token;

          let userData = {
            id,
            fname,
            lname,
            email,
            phone,
            token,
            timestamp: new Date().toString(),
          };
          let appState = {
            isLoggedIn: true,
            user: userData,
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            isLoggedIn: appState.isLoggedIn,
            user: appState.user,
            token: appState.token,
          });
          // redirect home
          this.props.history.push("/admin/dashboard");
        } else swal("Error!", json.data.message, "error");
        $("#login-form button").removeAttr("disabled").html("Login");
      })
      .catch((error) => {
        // alert(`An Error Occured! ${error}`);
        swal("Login Failed!", "" + error.response.data.message, "error", {
          button: false,
          timer: 1500,
        });
        $("#login-form button").removeAttr("disabled").html("Login");
      });
  };

  _loginUser = (email, password) => {
    $("#login-form button")
      .attr("disabled", "disabled")
      .html(
        '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Logging In...</span>'
      );
    var formData = new FormData();
    formData.append("email", email);
    formData.append("password", password);

    axios
      .post(`${axios.defaults.baseURL}/login`, formData)
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          // alert(json.data.message);
          swal("Success!", json.data.message, "success", {
            button: false,
            timer: 1500,
          });
          const { id, fname, lname, email, phone } = json.data.user;
          const { token } = json.data.token;

          let userData = {
            id,
            fname,
            lname,
            email,
            phone,
            token,
            timestamp: new Date().toString(),
          };
          let appState = {
            isLoggedIn: true,
            user: userData,
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            isLoggedIn: appState.isLoggedIn,
            user: appState.user,
            token: appState.token,
          });
          // redirect home
          this.props.history.push("/dashboard");
        } else swal("Error!", json.data.message, "error");
        $("#login-form button").removeAttr("disabled").html("Login");
      })
      .catch((error) => {
        // alert(`An Error Occured! ${error}`);
        swal("Login Failed!", "" + error.response.data.message, "error", {
          button: false,
          timer: 1500,
        });
        $("#login-form button").removeAttr("disabled").html("Login");
      });
  };

  render() {
    return (
      <Switch data="data">
        <div id="main">
          <Route exact path="/" component={Home} />
          <Route exact path="/membership" component={Membership} />
          <Route exact path="/about" component={About} />
          <Route exact path="/events" component={Event} />
          <Route exact path="/benefits" component={Services} />
          <Route exact path="/contact" component={Contact} />
          <Route exact path="/privacy" component={Privacy} />

          <Route
            exact
            path="/login"
            component={(props) => (
              <Login {...props} loginUser={this._loginUser} />
            )}
          />
          {/* <Route
            path="/register"
            component={(props) => (
              <Register {...props} registerUser={this._registerUser} />
            )}
          /> */}
          <Route
            path="/register"
            component={(props) => (
              <Register {...props}/>
            )}
          />

          <PrivateRoute
            exact
            path="/dashboard"
            component={(props) => <Dashboard {...props} />}
          />
          <PrivateRoute
            exact
            path="/referrals"
            component={(props) => <Referrals {...props} />}
          />
          <PrivateRoute exact path="/profile" component={Profile} />

          {/* Admin Routes */}
          <Route
            exact
            path="/admin"
            component={(props) => (
              <AdminLogin {...props} loginAdmin={this._loginAdmin} />
            )}
          />
          <PrivateRoute
            exact
            path="/admin/dashboard"
            component={(props) => <AdminDashboard {...props} />}
          />
          <PrivateRoute
            exact
            path="/admin/profile"
            component={(props) => <AdminProfile {...props} />}
          />

          <PrivateRoute
            exact
            path="/admin/users-list"
            component={(props) => <UsersList {...props} />}
          />
          <PrivateRoute
            path="/admin/user-details"
            component={(props) => <UserDetails {...props} />}
          />
        </div>
      </Switch>
    );
  }
}

const AppContainer = withRouter((props) => <App {...props} />);
// //console.log(store.getState())
render(
  <BrowserRouter>
    <AppContainer />
  </BrowserRouter>,

  document.getElementById("root")
);
