import Img37 from "./assets/images/gallery/37.jpg";
import Img38 from "./assets/images/gallery/38.jpg";
import Img40 from "./assets/images/gallery/40.jpg";

var _name, _tags;

const IMAGES =
[
    {
        src: Img1,
        thumbnail: Img1,
        className: "gallery-image",
        // tags: [{value: "Niyi"}],
        caption: ""
    },
    {
        src: Img2,
        thumbnail: Img2,
        className: "gallery-image",
        // tags: [{value: "Niyi"}],
        caption: ""
    },
    {
        src: Img3,
        thumbnail: Img3,
        className: "gallery-image",
        // tags: [{value: "Niyi"}],
        caption: ""
    },
    {
        src: Img4,
        thumbnail: Img4,
        className: "gallery-image",
        // tags: [{value: "Niyi"}],
        caption: ""
    }
]


export default class Galleri extends Component {
    constructor(props) {
        super(props);
        // let { user } = this.props.appstate;
        this.state = {
          imageSource: '',
          file: null,
          tributes: [],
          currentPage: 1,
          tributesPerPage: 50
        };
        this.onChange = this.onChange.bind(this);
      }

      componentDidMount() {
        this.fetchTributes();
      }

      fetchTributes = e => {
        axios
          .get(`${axios.defaults.baseURL}/photos`)
          .then(response => {
            console.log(response);
            return response;
          })
          .then(json => {
            if (json.data.success) {
              this.setState({ tributes: json.data.tributes });
            } else alert("Fetching Tributes Failed!");
          })
          .catch(error => {
            console.error(`An Error Occured! ${error}`);
          });
      }

      handleTributePost = e => {
        e.preventDefault();

        var formData = new FormData();
        formData.append("name", _name.value);
        formData.append("photo", this.state.file);
        formData.append("tags", _tags.value);

        console.log(formData);

      axios({
          method: 'post',
          url: `${axios.defaults.baseURL}/photo`,
          data: formData,
            headers: {'Content-Type': 'multipart/form-data' }
            })
        .then(response => {
          console.log(response);
          return response;
        })
        .then(json => {
          const { id, name, photo, tags } = json.data.tribute;
          let tributeData = {
            id,
            name,
            photo,
            tags,
            timestamp: new Date().toString()
          };
          let appState = {
            tribute: tributeData
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            tribute: appState.tribute,
          });

          if (json.data.success === true) {
            swal("Published!", "" + json.data.message, "success", {
              button: false,
              timer: 1500
            });
            $("#name-input").val('');
            $("#photo-input").val('');
            $("#tag-input").val('');
            this.fetchTributes();
            // redirect home
            // this.props.history.push("/dashboard");
          } else {
            // alert(json.data.message);
            swal("Alert!", "" + json.data.message, "warning");
          }

        })
        .catch(error => {
          // alert("An Error Occured!" + error);
          console.log(error.response.data);
          swal("Warning!", "" + error.response.data.message.tribute, "warning");
        });

      };

    uploadPhoto = e => {
    this.photoUpload.click();
    };

    onChange = event => {
    this.setState({ imageSource: '', imageData: '', file:event.target.files[0] });
    const file = event.target.files[0];
    console.log(file);

    if (file) {
        let reader = new FileReader();
        reader.readAsDataURL(event.target.files[0]);
        reader.onload = event => {
        this.setState({
            imageSource: event.target.result,
            imageData: file
        });
        };
    }
    };

    render() {
    const { currentPage, tributesPerPage } = this.state;

        // Logic for displaying tributes
        const tributesList = this.state.tributes;
        const indexOfLastTribute = currentPage * tributesPerPage;
        const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
        const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

        const renderTributes = currentTributes.map((tribute, index) => {
        return (
                <img src={tribute.photo} key={tribute.id} className="gallery-image" alt=""/>
        );
        });
        return (
            <div>
                <Header/>
                <div className="center-canvas">
                <div className="center-content"><br/>
                    <h4>Gallery <img src={Pen} className="pen" alt=""/></h4>
                </div>
                <div className="gallery-section">
                    {renderTributes}
                    <Gallery images={IMAGES}/>
                </div>
                <div><br/><br/>
                <div className="post-tribute-card">
                <h4 className="text-white">Upload Image <img src={Pen} className="pen" alt=""/></h4><br/>
                        <div onClick={this.uploadPhoto} className="center-content">
                            <img
                                className="img-holder"
                                src={
                                this.state.imageSource
                                    ? this.state.imageSource
                                    : Avatar
                                }
                                alt="user-avatar"
                            /><br/><br/>
                        </div>
                        <div className="post-tribute-section">
                        <div className="story-entry-form">
                            <form onSubmit={this.handleTributePost} method="post">
                            <div class="form-group">
                                <input ref={input => (this.photoUpload = input)} onChange= {this.onChange} name="photo" type="file" class="form-control image-input" id="photo-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_name = input)} name="name"  placeholder="Please enter your name" type="text" class="form-control" id="name-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_tags = input)} name="tags"  placeholder="Tag friends and family, separate with comma" type="text" class="form-control" id="tag-input"/>
                            </div>
                            <button type="submit" class="btn btn-primary float-right">Upload Photo</button>
                            </form>
                        </div>
                        </div>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}
