import React, { Component } from 'react'
import axios from "axios";
// import { Link } from "react-router-dom";
// import Sound from 'react-sound';
import swal from 'sweetalert';
import $ from "jquery";


import Header from './common/Header'

import Pen from "./assets/images/icons/gallery.png";
import Avatar from "./assets/images/avatar.png";
import Img1 from "./assets/images/gallery/1.jpg";
import Img2 from "./assets/images/gallery/2.jpg";
import Img3 from "./assets/images/gallery/3.jpg";
import Img4 from "./assets/images/gallery/4.jpg";
import Img5 from "./assets/images/gallery/5.jpg";
import Img6 from "./assets/images/gallery/6.jpg";
import Img7 from "./assets/images/gallery/7.jpg";
import Img8 from "./assets/images/gallery/8.jpg";
import Img9 from "./assets/images/gallery/9.jpg";
import Img10 from "./assets/images/gallery/10.jpg";
import Img11 from "./assets/images/gallery/11.jpg";
import Img12 from "./assets/images/gallery/12.jpg";
import Img13 from "./assets/images/gallery/13.jpg";
import Img14 from "./assets/images/gallery/14.jpg";
import Img15 from "./assets/images/gallery/15.jpg";
import Img16 from "./assets/images/gallery/16.jpg";
import Img17 from "./assets/images/gallery/17.jpg";
import Img18 from "./assets/images/gallery/18.jpg";
import Img19 from "./assets/images/gallery/19.jpg";
import Img20 from "./assets/images/gallery/20.jpg";
import Img21 from "./assets/images/gallery/21.jpg";
import Img22 from "./assets/images/gallery/22.jpg";
import Img23 from "./assets/images/gallery/23.jpg";
import Img24 from "./assets/images/gallery/24.jpg";
import Img25 from "./assets/images/gallery/25.jpg";
import Img26 from "./assets/images/gallery/26.jpg";
import Img27 from "./assets/images/gallery/27.jpg";
import Img28 from "./assets/images/gallery/28.jpg";
import Img29 from "./assets/images/gallery/29.jpg";
import Img30 from "./assets/images/gallery/30.jpg";
import Img31 from "./assets/images/gallery/31.jpg";
import Img32 from "./assets/images/gallery/32.jpg";
import Img33 from "./assets/images/gallery/33.jpg";
import Img34 from "./assets/images/gallery/34.jpg";
import Img35 from "./assets/images/gallery/35.jpg";
import Img36 from "./assets/images/gallery/36.jpg";
import Img37 from "./assets/images/gallery/37.jpg";
import Img38 from "./assets/images/gallery/38.jpg";
import Img40 from "./assets/images/gallery/40.jpg";

var _name, _tags;

export default class Gallery extends Component {
    constructor(props) {
        super(props);
        // let { user } = this.props.appstate;
        this.state = {
          imageSource: '',
          file: null,
          tributes: [],
          currentPage: 1,
          tributesPerPage: 50
        };
        this.onChange = this.onChange.bind(this);
      }

      componentDidMount() {
        this.fetchTributes();
      }

      fetchTributes = e => {
        axios
          .get(`${axios.defaults.baseURL}/photos`)
          .then(response => {
            console.log(response);
            return response;
          })
          .then(json => {
            if (json.data.success) {
              this.setState({ tributes: json.data.tributes });
            } else alert("Fetching Tributes Failed!");
          })
          .catch(error => {
            console.error(`An Error Occured! ${error}`);
          });
      }

      handleTributePost = e => {
        e.preventDefault();

        var formData = new FormData();
        formData.append("name", _name.value);
        formData.append("photo", this.state.file);
        formData.append("tags", _tags.value);

        console.log(formData);

      axios({
          method: 'post',
          url: `${axios.defaults.baseURL}/photo`,
          data: formData,
            headers: {'Content-Type': 'multipart/form-data' }
            })
        .then(response => {
          console.log(response);
          return response;
        })
        .then(json => {
          const { id, name, photo, tags } = json.data.tribute;
          let tributeData = {
            id,
            name,
            photo,
            tags,
            timestamp: new Date().toString()
          };
          let appState = {
            tribute: tributeData
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            tribute: appState.tribute,
          });

          if (json.data.success === true) {
            swal("Published!", "" + json.data.message, "success", {
              button: false,
              timer: 1500
            });
            $("#name-input").val('');
            $("#photo-input").val('');
            $("#tag-input").val('');
            this.fetchTributes();
            // redirect home
            // this.props.history.push("/dashboard");
          } else {
            // alert(json.data.message);
            swal("Alert!", "" + json.data.message, "warning");
          }

        })
        .catch(error => {
          // alert("An Error Occured!" + error);
          console.log(error.response.data);
          swal("Warning!", "" + error.response.data.message.tribute, "warning");
        });

      };

    uploadPhoto = e => {
    this.photoUpload.click();
    };

    onChange = event => {
    this.setState({ imageSource: '', imageData: '', file:event.target.files[0] });
    const file = event.target.files[0];
    console.log(file);

    if (file) {
        let reader = new FileReader();
        reader.readAsDataURL(event.target.files[0]);
        reader.onload = event => {
        this.setState({
            imageSource: event.target.result,
            imageData: file
        });
        };
    }
    };

    render() {
    const { currentPage, tributesPerPage } = this.state;

        // Logic for displaying tributes
        const tributesList = this.state.tributes;
        const indexOfLastTribute = currentPage * tributesPerPage;
        const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
        const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

        const renderTributes = currentTributes.map((tribute, index) => {
        return (
                <img src={tribute.photo} key={tribute.id} className="gallery-image" alt=""/>
        );
        });
        return (
            <div>
                <Header/>
                <div className="center-canvas">
                <div className="center-content"><br/>
                    <h4>Gallery <img src={Pen} className="pen" alt=""/></h4>
                </div>
                <div>
                <div className="post-tribute-card">
                <h4 className="text-white">Upload Image <img src={Pen} className="pen" alt=""/></h4><br/>
                        <div onClick={this.uploadPhoto} className="center-content">
                            <img
                                className="img-holder"
                                src={
                                this.state.imageSource
                                    ? this.state.imageSource
                                    : Avatar
                                }
                                alt="user-avatar"
                            /><br/><br/>
                        </div>
                        <div className="post-tribute-section">
                        <div className="story-entry-form">
                            <form onSubmit={this.handleTributePost} method="post">
                            <div class="form-group">
                                <input ref={input => (this.photoUpload = input)} onChange= {this.onChange} name="photo" type="file" class="form-control image-input" id="photo-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_name = input)} name="name"  placeholder="Please enter your name" type="text" class="form-control" id="name-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_tags = input)} name="tags"  placeholder="Tag friends and family, separate with comma" type="text" class="form-control" id="tag-input"/>
                            </div>
                            <button type="submit" class="btn btn-primary float-right">Upload Photo</button>
                            </form>
                        </div>
                        </div>
                    </div>
                </div>
                <div className="gallery-section">
                    {/* <a className="lightbox" href="#Img1">
                        <img src={Img1} className="gallery-image"/>
                    </a>
                    <div className="lightbox-target" id="Img1">
                        <img src={Img1}/>
                        <a className="lightbox-close" href="#"></a>
                    </div> */}
                    {renderTributes}
                    <img src={Img1} className="gallery-image" alt=""/>
                    <img src={Img2} className="gallery-image" alt=""/>
                    <img src={Img3} className="gallery-image" alt=""/>
                    <img src={Img4} className="gallery-image" alt=""/>
                    <img src={Img5} className="gallery-image" alt=""/>
                    <img src={Img6} className="gallery-image" alt=""/>
                    <img src={Img7} className="gallery-image" alt=""/>
                    <img src={Img8} className="gallery-image" alt=""/>
                    <img src={Img9} className="gallery-image" alt=""/>
                    <img src={Img10} className="gallery-image" alt=""/>
                    <img src={Img11} className="gallery-image" alt=""/>
                    <img src={Img12} className="gallery-image" alt=""/>
                    <img src={Img13} className="gallery-image" alt=""/>
                    <img src={Img14} className="gallery-image" alt=""/>
                    <img src={Img15} className="gallery-image" alt=""/>
                    <img src={Img16} className="gallery-image" alt=""/>
                    <img src={Img17} className="gallery-image" alt=""/>
                    <img src={Img18} className="gallery-image" alt=""/>
                    <img src={Img19} className="gallery-image" alt=""/>
                    <img src={Img20} className="gallery-image" alt=""/>
                    <img src={Img21} className="gallery-image" alt=""/>
                    <img src={Img22} className="gallery-image" alt=""/>
                    <img src={Img23} className="gallery-image" alt=""/>
                    <img src={Img24} className="gallery-image" alt=""/>
                    <img src={Img25} className="gallery-image" alt=""/>
                    <img src={Img26} className="gallery-image" alt=""/>
                    <img src={Img27} className="gallery-image" alt=""/>
                    <img src={Img28} className="gallery-image" alt=""/>
                    <img src={Img29} className="gallery-image" alt=""/>
                    <img src={Img30} className="gallery-image" alt=""/>
                    <img src={Img31} className="gallery-image" alt=""/>
                    <img src={Img32} className="gallery-image" alt=""/>
                    <img src={Img33} className="gallery-image" alt=""/>
                    <img src={Img34} className="gallery-image" alt=""/>
                    <img src={Img35} className="gallery-image" alt=""/>
                    <img src={Img36} className="gallery-image" alt=""/>
                    <img src={Img37} className="gallery-image" alt=""/>
                    <img src={Img38} className="gallery-image" alt=""/>
                    <img src={Img40} className="gallery-image" alt=""/>
                </div>
                </div>
            </div>
        )
    }
}
