import React, { Component } from "react";
import {Link} from 'react-router-dom';

export class AdminMenuList extends Component {
  render() {

    let location = window.location.href.split('/')[window.location.href.split('/').length - 1];

    console.log(location);

    const dashboardClass = location === "admin/dashboard" ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary" : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";

    const evestClass = location === "admin/evest-list" ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary" : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";


    const landvestClass = location === "admin/landvest-list" ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary" : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";


    const plansClass = location === "admin/my-plans" ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary" : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";


    const accountClass = location === "admin/my-account" ? "btn w-100 text-left check-hover py-3 bg-transparent router-link-exact-active router-link-active btn-secondary" : "btn w-100 text-left check-hover py-3 bg-transparent btn-secondary";


    return (
      <div>
        <ul className="list-unstyled">
          <li>
            <Link
              to="/admin/dashboard"
              className={dashboardClass}
            >
              <i className="mr-1 ti-dashboard"></i> Dashboard
            </Link>
          </li>
          <li>
            <Link
              to="/admin/evest-list"
              className={evestClass}
            >
              <i className="mr-1 ti-home"></i> EVest
            </Link>
          </li>
          <li>
            <Link
              to="/admin/landvest-list"
              className={landvestClass}
            >
              <i className="mr-1 ti-map"></i> LandVest
            </Link>
          </li>
          <li>
            <Link
              to="/admin/my-account"
              className={accountClass}
            >
              <i className="mr-1 ti-user"></i> Admin Profile
            </Link>
          </li>
        </ul>
      </div>
    );
  }
}

export default AdminMenuList;
