import React from "react";
import axios from "axios";
// import { Link } from "react-router-dom";
// import Sound from 'react-sound';
import swal from 'sweetalert';
import $ from "jquery";

import Header from "./common/Header.js";
import Pen from "./assets/images/icons/pen.png";

var _name, _tags, _tribute;

class Story extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      tributes: [],
      currentPage: 1,
      tributesPerPage: 20,
      query: ""
    };
  }

  handleClick(event) {
    event.preventDefault();
    this.setState({
      currentPage: Number(event.target.id)
    });
    $("#"+`${event.target.id}`).attr('class', 'pg-link active-link');
  }


  componentDidMount() {
    this.fetchTributes();
  }

  queryField(e) {
    e.preventDefault();
    this.setState({
      query: event.target.value
    })
  }

  searchTributes = e => {
    axios
      .get(`${axios.defaults.baseURL}/story/${this.state.query}`)
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ tributes: json.data.tributes });
        } else alert("Fetching Tributes Failed!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  fetchTributes = e => {
    axios
      .get(`${axios.defaults.baseURL}/stories`)
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ tributes: json.data.tributes });
        } else alert("Fetching Tributes Failed!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  handleTributePost = e => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("name", _name.value);
    formData.append("tags", _tags.value);
    formData.append("story", _tribute.value);

    console.log(formData);

  axios({
      method: 'post',
      url: `${axios.defaults.baseURL}/story`,
      data: formData
      })
    .then(response => {
      console.log(response);
      return response;
    })
    .then(json => {
      const { id, name, tags, tribute } = json.data.tribute;
      let tributeData = {
        id,
        name,
        tags,
        tribute,
        timestamp: new Date().toString()
      };
      let appState = {
        tribute: tributeData
      };
      // save app state with user date in local storage
      localStorage["appState"] = JSON.stringify(appState);
      this.setState({
        tribute: appState.tribute,
      });

      if (json.data.success === true) {
        swal("Published!", "" + json.data.message, "success", {
          button: false,
          timer: 1500
        });
        $("#name-input").val('');
        $("#tribute-input").val('');
        $("#tag-input").val('');
        this.fetchTributes();
        // redirect home
        // this.props.history.push("/dashboard");
      } else {
        // alert(json.data.message);
        swal("Alert!", "" + json.data.message, "warning");
      }

    })
    .catch(error => {
      // alert("An Error Occured!" + error);
      console.log(error.response.data);
      swal("Warning!", "" + error.response.data.message.tribute, "warning");
    });

  };


  render() {

    const { tributes, currentPage, tributesPerPage } = this.state;

    // Logic for displaying tributes
    const tributesList = this.state.tributes;
    const indexOfLastTribute = currentPage * tributesPerPage;
    const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
    const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

    const renderTributes = currentTributes.map((tribute, index) => {
      return (
          <div className="v-card" key={tribute.id}>
            <div className="tribute-card">
            <div className="post-content">
                  <p className="poster-name">Posted by {tribute.name} on {tribute.date}</p>
                  <p className="post">{tribute.story}</p>
                  <p className="tags float-right"><b>Tagged in this Story:</b> {tribute.tags}</p>
                  </div>
            </div>
          </div>
      );
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(tributes.length / tributesPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li
          className="pg-link"
          key={number}
          id={number}
          onClick={this.handleClick}
        >
          {number}
        </li>
      );
    });


    return (
      <div>
        <Header/>

        <div className="center-canvas">
          <div className="center-content"><br/>
            <h4>Stories <img src={Pen} className="pen" alt=""/></h4>
          </div>
            {renderTributes}
            <div className="post-tribute-card">
                <h4 className="text-blue">Write a Story <img src={Pen} className="pen" alt=""/></h4><br/>
                <div className="post-tribute-section">
                  <div className="story-entry-form">
                    <form onSubmit={this.handleTributePost} method="post">
                      <div class="form-group">
                        <textarea  ref={input => (_tribute = input)}  placeholder="Write your story here..." name="tribute" type="text" class="form-control" maxlength="25000" cols="20" rows="10" id="tribute-input" required/>
                      </div>
                      <div class="form-group">
                        <input ref={input => (_name = input)} name="name"  placeholder="Please enter your name" type="text" class="form-control" id="name-input" required/>
                      </div>
                      <div class="form-group">
                        <input ref={input => (_tags = input)} name="tags"  placeholder="Tag friends and family, separate with comma" type="text" class="form-control" id="tag-input" required/>
                      </div>
                      <button type="submit" class="btn btn-primary float-right">Publish Story</button>
                    </form>
                  </div>
                </div>
                <br/>
                <br/>
              </div>
        </div>
      </div>
    );
  }
}
// module.exports = Tribute;

export default Story;
