import React, { Component} from "react";

import Header from "./common/Header";
import { Link } from "react-router-dom";
import Footer from "./common/Footer";
import { PaystackConsumer } from "react-paystack";

import ScriptTag from "react-script-tag";
import AOS from "aos";

import QRCode from "qrcode.react";

import axios from "axios";
import swal from "sweetalert";

import CardIcon from "./assets/images/gold-card-image.png";
import Avatar from "./assets/img/default-user-avatar.png";

export class Expired extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
    };
    AOS.init();
  }

  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            user: json.data.user[0],
          });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }

  render() {

    const configNGN = {
        // reference: (new Date()).getTime(),
        email: "hipheckt@gmail.com",
        // plan: this.state.plan,
        amount: "100000" + "00",
        currency: "NGN",
        // publicKey: 'pk_live_68d2b6e9edacd11392f5548db9047ffb4911b39d',
        publicKey: "pk_test_a842c66ae323671a5e629e8970958560b65d3ad6",
      };

      const componentPropsNGN = {
        ...configNGN,
        text: "Sapio Sibling Registration",
        onSuccess: (response) => {
          // console.log(response);

          this.setState({
            payment_ref: response.trxref,
          });

          axios({
            method: "get",
            url: `https://api.paystack.co/transaction/verify/${this.state.payment_ref}`,
            // headers: {'Authorization': `Bearer sk_live_5299c2d2e25663425657490251d1cec7d8c206aa`, 'Content-Type': 'application/json' }
            headers: {
              Authorization: `Bearer sk_test_b921c5ae7960bc5df7b895cad6b1f37ce0b29b73`,
              "Content-Type": "application/json",
            },
          })
            .then((response) => {
              console.log(response);
              return response;
            })
            .then((json) => {



              axios
                .post(`${axios.defaults.baseURL}/renew`)
                .then((response) => {
                  //console.log(response);
                  return response;
                })
                .then((json) => {
                  if (json.data.success) {
                    // alert(`Registration Successful!`);
                    swal("Success!", json.data.message, "success", {
                      button: false,
                      timer: 1500,
                    });
                    // redirect home
                    this.props.history.push("/dashboard");
                  } else {
                    // alert(`Registration Failed!`);
                    swal("Failed", "Renewal Failed", "error");
                  }
                })
                .catch((error) => {
                  // alert("An Error Occured!" + error);
                  swal("Error!", "An Error Occured", "error", {
                    button: false,
                    // timer: 1500
                  });
                });
            });
        },
        onClose: () => null,
      };

    return (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <br />
            <div className="flex-copy-lockup">
              <div className="copy-block align-text col" style={{textAlign:"center"}}>
                <h4>
                  Hi {this.state.user.fname} {this.state.user.lname} üòÅ, Your Membership has Expired!
                </h4>
                <p>Click the button below to Renew Membership (N100,000)!</p>

                <div className="ac-ln-action ac-ln-action-button" style={{marginBottom:"200px"}}>
                <PaystackConsumer {...componentPropsNGN}>
                      {({ initializePayment }) => (
                        <button
                          type="button"
                          onClick={() =>
                            swal(
                              "You are about to pay a membership renewal fee of ‚Ç¶100,000",
                              {
                                buttons: {
                                  cancel: "No, Cancel",
                                  catch: {
                                    text: "Yes, Continue",
                                    value: "catch",
                                  },
                                },
                              }
                            ).then((value) => {
                              switch (value) {
                                case "catch":
                                  initializePayment();
                                  break;

                                default:
                              }
                            })
                          }
                          className="submit-button"
                          data-analytics-title="Renew The Sapio Club"
                        >
                          Renew Membership Now
                        </button>
                      )}
                    </PaystackConsumer>
                      </div>
              </div>
              {/* CARD */}

              {/* ENDCARD */}

            </div>
          </section>

          {/* <QRCode value="http://facebook.github.io/react/" /> */}
        </main>
        <Footer />
        {/* Template Main JS File */}
        <ScriptTag
          type="text/javascript"
          src="../assets/js/built.js"
        />
      </body>
    );
  }
}

export default Expired;
