import React, { Component } from 'react'
import Light from "../assets/images/light.png"
import { Link } from "react-router-dom";


import Play from "../assets/images/icons/play.png";
import Pause from "../assets/images/icons/pause.png";

export default class Header extends Component {
    constructor(props) {
    super(props);
      this.state = {
        play: false,
        pause: true,
      };
      this.url = "https://adeniyiogunwuyi.com/puff.mp3";
      this.audio = new Audio(this.url);
    }

    // componentDidMount(){
    //     this.audio.play();
    // }

    play = () => {
        this.setState({
            play:true, pause: false,
        });
        this.audio.play();
    }
    pause = () => {
        this.setState({
            play:false, pause: true,
        });
        this.audio.pause();
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark theme-bg">
                <Link className="navbar-brand" to="/"><img src={Light} className="light" alt="..."/> Henry Adeniyi Ogunwuyi</Link>
                        {this.state.pause === true ? (
                            <a onClick={this.play}>
                                <img src={Play} className="play-icon x-visible" alt=""/>
                            </a>
                        ) : (
                            <a onClick={this.pause}>
                                <img src={Pause} className="play-icon x-visible" alt=""/>
                            </a>
                        )}
                        {this.state.pause === true ? (
                            <a onClick={this.play}>
                                <img src={Play} className="play-icon m-visible" alt=""/>
                            </a>
                        ) : (
                            <a onClick={this.pause}>
                                <img src={Pause} className="play-icon m-visible" alt=""/>
                            </a>
                        )}
                </nav>
                <div className="menu-wrapper">
          <div className="center-content">
            <ul>
              <li><Link to="life">Life </Link></li>
              <li><Link to="gallery">Gallery</Link></li>
              <li><Link to="tributes">Tributes</Link></li>
              <li><Link to="stories">Stories </Link></li>
              <li><Link to="funeral-arrangements">Funeral</Link></li>
            </ul>
          </div>
        </div>
            </div>
        )
    }
}
