import React, { Component } from "react";
import { Link } from "react-router-dom";

import axios from "axios";
import Avatar from "../assets/img/default-user-avatar.png";


import swal from 'sweetalert';
import $ from "jquery";

// import "../assets/css/app.css";

import Menu1 from "../assets/img/menu-light-e.svg";
import Logo from "../assets/img/logo-light.svg";
import MenuList from "./Menu";

export default class Header extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
              ? JSON.parse(localStorage["appState"]).user.token
              : "",
            user: [],
    };
  }
  componentDidMount() {
    axios
            .get(`${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
            {
              headers: {
                'Authorization': `Bearer ${this.state.token}`
              }
            }
            )
            .then(response => {
              //console.log(response);
              return response;
            })
            .then(json => {
              if (json.data.success) {
                this.setState({ user: json.data.user[0] });
              // } else alert("Cannot Fetch Data!");
              } else swal("Oops!", "Cannot Fetch Data!", "error");;
            })
            .catch(error => {
              console.error(`An Error Occured! ${error}`);
            });
            // alert(id)
  }
  logoutUser = () => {
    localStorage.clear();
    window.location.href = '/login';
  };

  render() {

    // switch function for login/signup
    const mobileMenuOpen = e => {
        $("#mobile-navbar").addClass('open-mobile-menu');
        $(".mobile-menu-overlay").addClass('show-mobile-menu-overlay');
    };

    const mobileMenuClose = e => {
        $("#mobile-navbar").removeClass('open-mobile-menu');
        $(".mobile-menu-overlay").removeClass('show-mobile-menu-overlay');
    };

    return (
      <>


<meta name="ac-gn-store-key" content="SFX9YPYY9PPXCU9KH" />
<input type="checkbox" id="ac-gn-menustate" className="ac-gn-menustate" />
<nav id="ac-globalnav" className="no-js" role="navigation" aria-label="Global" data-hires="false" data-analytics-region="global nav" lang="en-NG" dir="ltr" data-search-locale="en_NG" data-search-suggestions-api="/search-services/suggestions/" data-search-defaultlinks-api="/search-services/suggestions/defaultlinks/">
	<div className="ac-gn-content">
		<ul className="ac-gn-header">
			<li className="ac-gn-item ac-gn-menuicon">
				<label className="ac-gn-menuicon-label" for="ac-gn-menustate" aria-hidden="true">
					<span className="ac-gn-menuicon-bread ac-gn-menuicon-bread-top">
						<span className="ac-gn-menuicon-bread-crust ac-gn-menuicon-bread-crust-top"></span>
					</span>
					<span className="ac-gn-menuicon-bread ac-gn-menuicon-bread-bottom">
						<span className="ac-gn-menuicon-bread-crust ac-gn-menuicon-bread-crust-bottom"></span>
					</span>
				</label>
				<a href="#ac-gn-menustate" role="button" className="ac-gn-menuanchor ac-gn-menuanchor-open" id="ac-gn-menuanchor-open">
					<span className="ac-gn-menuanchor-label">Global Nav Open Menu</span>
				</a>
				<a href="#" role="button" className="ac-gn-menuanchor ac-gn-menuanchor-close" id="ac-gn-menuanchor-close">
					<span className="ac-gn-menuanchor-label">Global Nav Close Menu</span>
				</a>
			</li>
			<li className="ac-gn-item ac-gn-apple">
				<a className="ac-gn-link ac-gn-link-apple" href="/ng/" data-analytics-title="apple home" id="ac-gn-firstfocus-small">
					<span className="ac-gn-link-text">SapioClub</span>
				</a>
			</li>
		</ul>
		<ul className="ac-gn-list">
			<li className="ac-gn-item ac-gn-apple">
				<a className="ac-gn-link ac-gn-link-apple" href="/" data-analytics-title="Home" id="ac-gn-firstfocus">
						{/* <span>Home</span> */}
				</a>
			</li>
			<li className="ac-gn-item ac-gn-item-menu">
				<a className="ac-gn-link" href="/ng/mac/" data-analytics-title="About the Club">
						<span>About the Club</span>
					</a>
			</li>
			<li className="ac-gn-item ac-gn-item-menu">
				<a className="ac-gn-link" href="/ng/ipad/" data-analytics-title="Membership">
						<span>Membership</span>
					</a>
