import React, { Component } from "react";
import axios from "axios";
// import { Link } from "react-router-dom";
import swal from 'sweetalert';
import $ from "jquery";

import Header from "./common/Header";
import Sidebar from "./common/Sidebar";
import { Link } from "react-router-dom";

export class Evest extends Component {
  constructor(props) {
    super(props);
    this.state = {
      homes: [],
      currentPage: "",
      homesPerPage: 1,
      query: "",
    };
  }

  queryField = (e) => {
    e.preventDefault();
    this.setState({
      query: e.target.value,
    });
    axios
      .get(`${axios.defaults.baseURL}/search-homes/${this.state.query}`)
      .then((response) => {
        console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ homes: json.data.homes });
        } else alert("Fetching Homes Failed!");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  };

  handleClick(event) {
    event.preventDefault();
    this.setState({
      currentPage: Number(event.target.id),
    });
    $("#" + `${event.target.id}`).attr("class", "pg-link active-link");
  }

  componentDidMount() {
    this.fetchHomes();
  }

  fetchHomes = (e) => {
    axios
      .get(`${axios.defaults.baseURL}/homes`)
      .then((response) => {
        console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ homes: json.data.homes });
        } else alert("Fetching Homes Failed!");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  };

  render() {
    const { homes, currentPage, homesPerPage } = this.state;

    // Logic for displaying homes
    const homesList = this.state.homes;
    const indexOfLastHome = currentPage * homesPerPage;
    const indexOfFirstHome = indexOfLastHome - homesPerPage;
    const currentHomes = homesList.slice(indexOfFirstHome, indexOfLastHome);

    const renderHomes = currentHomes.map((home, index) => {
      return (
        <div
          class="mb-5 col-md-6 col-lg-6 col-xl-4 col-12 order-2"
          key={home.id}
        >
          <div class="wrap dash-card-main bg-white position-relative">
            <div class="wrap px-2 position-absolute w-100">
              <div class="dash-card-main thumb bg-dark mt-2 shadow-xl bg-cover">
                <img
                  src={home.image}
                  className="plan-img"
                  alt=""
                />
              </div>
            </div>
            <br />
            <br />
            <br />
            <br />
            <br />
            <div class="wrap p-4 pt-5">
              <div class="row mt-1 mb-2 pt-5">
                <div class="pt-2 col">
      <h6 class="text-primary mb-0">â‚¦{home.price} {home.type}</h6>
                  <span class="badge text-light badge-primary">{home.duration} Months</span>
                </div>
              </div>
            </div>
            <hr class="mt-0"></hr>
            <div class="wrap px-4 pb-4">
              <div class="row no-gutters">
                <div class="col">
                  <Link to="/view-home" class="btn w-100 btn-primary">
                    View
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(homes.length / homesPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map((number) => {
      return (
        <li
          className="pg-link"
          key={number}
          id={number}
          onClick={this.handleClick}
        >
          {number}
        </li>
      );
    });

    return (
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="page-title">
                    <div className="row text-primary py-2 pt-4">
                      <div className="col-10">
                        <div
                          className="user-welcome"
                          style={{ overflow: "hidden" }}
                        >
                          <div className="wrapper d-flex">
                            <h4 className="d-inline-block">EVest</h4>
                          </div>
                          <p className="text-secondary">
                            Available Homes Listing
                          </p>
                        </div>
                      </div>
                      <div className="text-right col-2"></div>
                    </div>
                  </div>
                  <div class="row mt-4 align-items-stretch">
                    {/* Product Listing */}
                      {renderHomes}
                    {/* End Product Listing */}
                  </div>
                </div>
                  <div className="text-center">
                    {renderPageNumbers}
                  </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Evest;
