        this.onChange = this.onChange.bind(this);
      }

      componentDidMount() {
        this.fetchTributes();
      }

      fetchTributes = e => {
        axios
          .get(`${axios.defaults.baseURL}/photos`)
          .then(response => {
            console.log(response);
            return response;
          })
          .then(json => {
            if (json.data.success) {
              this.setState({ tributes: json.data.tributes });
            } else alert("Fetching Tributes Failed!");
          })
          .catch(error => {
            console.error(`An Error Occured! ${error}`);
          });
