      handleTributePost = e => {
        e.preventDefault();

        var formData = new FormData();
        formData.append("name", _name.value);
        formData.append("photo", this.state.file);
        formData.append("tags", _tags.value);

        console.log(formData);

      axios({
          method: 'post',
          url: `${axios.defaults.baseURL}/photo`,
          data: formData,
            headers: {'Content-Type': 'multipart/form-data' }
            })
        .then(response => {
          console.log(response);
          return response;
        })
        .then(json => {
          const { id, name, photo, tags } = json.data.tribute;
          let tributeData = {
            id,
            name,
            photo,
            tags,
            timestamp: new Date().toString()
          };
          let appState = {
            tribute: tributeData
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            tribute: appState.tribute,
          });

          if (json.data.success === true) {
            swal("Published!", "" + json.data.message, "success", {
              button: false,
              timer: 1500
            });
            $("#name-input").val('');
            $("#photo-input").val('');
            $("#tag-input").val('');
            this.fetchTributes();
            // redirect home
            // this.props.history.push("/dashboard");
          } else {
            // alert(json.data.message);
            swal("Alert!", "" + json.data.message, "warning");
          }

