import React from "react";
import axios from "axios";
// import { Link } from "react-router-dom";
// import Sound from 'react-sound';
import swal from 'sweetalert';

import Header from "./common/Header.js";

import Candle from "./assets/images/icons/candle_white.png";
import Rose from "./assets/images/icons/rose_white.png";
import Note from "./assets/images/icons/note_white.png";
import Candle2 from "./assets/images/icons/candle_blue.png";
import Rose2 from "./assets/images/icons/rose_blue.png";
import Note2 from "./assets/images/icons/note_blue.png";
import Pen from "./assets/images/icons/pen.png";

// import Song from "./assets/sound/beiber.mp3";

var _type, _name, _tribute;

class Tribute extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      tributes: [],
      currentPage: 1,
      tributesPerPage: 10,
      selectTribute: Candle2
    };
  }

  componentDidMount() {
    this.fetchTributes();
  }

  iconSwitch(e) {
    e.preventDefault();
    this.setState(
      {selectTribute: Note2}
    );
  }

  fetchTributes = e => {
    axios
      .get(`${axios.defaults.baseURL}/tributes`)
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ tributes: json.data.tributes });
        } else alert("Fetching Tributes Failed!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  handleTributePost = e => {
    e.preventDefault();

    var formData = new FormData();
    formData.append("type", "candle");
    // formData.append("type", _type.value);
    formData.append("name", _name.value);
    formData.append("tribute", _tribute.value);

    console.log(formData);

  axios({
      method: 'post',
      url: `${axios.defaults.baseURL}/tribute`,
      data: formData
      })
    .then(response => {
      console.log(response);
      return response;
    })
    .then(json => {
      const { id, type, name, tribute } = json.data.tribute;
      let tributeData = {
        id,
        type,
        name,
        tribute,
        timestamp: new Date().toString()
      };
      let appState = {
        tribute: tributeData
      };
      // save app state with user date in local storage
      localStorage["appState"] = JSON.stringify(appState);
      this.setState({
        tribute: appState.tribute,
      });

      if (json.data.success === true) {
        swal("Published!", "" + json.data.message, "success", {
          button: false,
          timer: 1500
        });
        $("#name-input")
        .attr("value", "");
        $("#tribute-input")
        .attr("value", "");
        this.fetchTributes();
        // redirect home
        // this.props.history.push("/dashboard");
      } else {
        // alert(json.data.message);
        swal("Alert!", "" + json.data.message, "warning");
      }

    })
    .catch(error => {
      // alert("An Error Occured!" + error);
      console.log(error.response.data);
      swal("Warning!", "" + error.response.data.message.tribute, "warning");
    });

  };


  render() {

    const { currentPage, tributesPerPage } = this.state;

    // Logic for displaying tributes
    const tributesList = this.state.tributes;
    const indexOfLastTribute = currentPage * tributesPerPage;
    const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
    const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

    const renderTributes = currentTributes.map((tribute, index) => {
      return (
          <div className="v-card" key={tribute.id}>
            <div className="tribute-card">
                <div className="post-icon">
                  {tribute.type === "candle" ? (
                  <img src={Candle2} className="tribute-icon" alt=""/>
                  ): tribute.type === "rose" ? (
                    <img src={Rose2} className="tribute-icon" alt=""/>
                    ): tribute.type === "note" ? (
                      <img src={Note2} className="tribute-icon" alt=""/>
                      ):
                  null}
                </div>
                <div className="post-content">
                  <p className="poster-name">Posted by {tribute.name} on {tribute.date}</p>
                  <p className="post">{tribute.tribute}</p>
                </div>
            </div>
          </div>
      );
    });

    return (
      <div>
        <Header/>

        <div className="center-canvas">
        <div className="post-tribute-card">
            <h4 className="text-white">Leave a Tribute <img src={Pen} className="pen" alt=""/></h4><br/>
            <div className="remove-top-margin center-content">
              <div className="icons-set">
                <div>
                    <img src={Candle} className="post-tribute-icon" alt=""/><br/>
                    <span>Light a Candle</span>
                </div>
                <div>
                    <img src={Rose} className="post-tribute-icon" alt=""/><br/>
                    <span>Lay a Flower</span>
                </div>
                <div>
                    <img src={Note} className="post-tribute-icon" alt=""/><br/>
                    <span>Leave a Note</span>
                </div>
              </div>
            </div>
            <div className="post-tribute-section">
              <div className="select-tribute">
                <img src={this.state.selectTribute} className="select-tribute-icon" alt=""/> 
              </div>
              <div className="entry-form">
                <form onSubmit={this.handleTributePost} method="post">
                  <div class="form-group">
                    <textarea  ref={input => (_tribute = input)}  placeholder="Add your tribute text here..." name="tribute" type="text" class="form-control" maxlength="15000" cols="20" rows="5" id="tribute"/>
                  </div>
                  <div class="form-group">
                    <input ref={input => (_name = input)} name="name"  placeholder="Please enter your name" type="text" class="form-control" id="name"/>
                  </div>
                  <button type="submit" class="btn btn-primary float-right">Publish Tribute</button>
                </form>
              </div>
            </div>
            <br/>
            <br/>
          </div>
          <div className="center-content"><br/>
            <h4>Tributes <img src={Pen} className="pen" alt=""/></h4>
          </div>
            {renderTributes}
        </div>
      </div>
    );
  }
}
// module.exports = Tribute;

export default Tribute;
