import React, { useState } from "react";
import Header from "./common/Header";
import Sidebar from "./common/Sidebar";
import { Document, Page } from 'react-pdf'
import ProgramPDF from './assets/program.pdf'

import { pdfjs } from 'react-pdf';
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

export default function Program() {

    const [numPages, setNumPages] = useState(null);
    const [pageNumber, setPageNumber] = useState(1);

    const nextPage = event => setPageNumber(pageNumber+1);

    const prevPage = event => setPageNumber(pageNumber-1);

    function onDocumentLoadSuccess({ numPages }) {
        setNumPages(numPages);
    }

    return (
        <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
            <Sidebar/>
            <Header/>
            <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="page-title container">
                    <div className="row text-primary py-2 pt-4">
                      <div className="col-10">
                        <div className="user-welcome" style={{overflow:"hidden"}}>
                          <div className="wrapper d-flex">
                            <h4 className="d-inline-block">My Plans</h4>
                          </div>
                        </div>
                      </div>
                      <div className="text-right col-2"></div>
                    </div>
                  </div>
                  <div className="container text-center mb-4">
                    <Document
                        file={ProgramPDF}
                        onLoadSuccess={onDocumentLoadSuccess}
                    >
                        <Page pageNumber={pageNumber} />
                    </Document>
                  </div>
                </div>
                <div className="text-center">
                    <div class="page-controls">
                        {pageNumber !== 1 ? (
                        <a className="pg-link" onClick={prevPage}>‹</a>
                        ) : null}
                            <span>{pageNumber} of {numPages}</span>
                        {pageNumber !== 10 ? (
                        <a className="pg-link" onClick={nextPage}>›</a>
                        ) : null}
                    </div><br/>
                    <a className="view-btn" href={ProgramPDF}>Download</a>
                </div>
                </div>
        </div>
        </div>
        </div>
        </div>
    )
}
