import React, { Component } from 'react'
import Light from "../assets/images/light.png"
import { Link } from "react-router-dom";

export default class Header extends Component {constructor(props) {
    super(props);
    this.state = {
      play: true,
      pause: false,
    };
    this.url = "https://adeniyiogunwuyi.com/beiber.mp3";
    this.audio = new Audio(this.url);
  }

  play = () => {
    this.setState({ play: true, pause: false })
      this.audio.play();
    }

    pause = () => {
    this.setState({ play: false, pause: true })
      this.audio.pause();
    }

    render() {
        return (
            <div>
                <nav className="navbar navbar-expand-lg navbar-dark theme-bg">
                <a className="navbar-brand" href="/"><img src={Light} className="light" alt="..."/> Henry Adeniyi Ogunwuyi</a>
                    {/* <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
                        <span className="navbar-toggler-icon"></span>
                    </button> */}
                </nav>
                <div className="menu-wrapper">
          <div className="center-content">
            <ul>
              <li><Link to="life">Life </Link></li>
              <li><Link to="gallery">Gallery</Link></li>
              <li><Link to="tributes">Tributes</Link></li>
              <li><Link to="stories">Stories </Link></li>
              <li><Link to="funeral-arrangements">Funeral</Link></li>
            </ul>
          </div>
        </div>
        <div>
      <button onClick={this.play}>Play</button>
      <button onClick={this.pause}>Pause</button>
    </div>
            </div>
        )
    }
}
