  // }
  // logoutUser = () => {
  //   localStorage.clear();
  //   window.location.href = "/login";
  // };

  render() {

    return (
      <>
        <footer id="ac-globalfooter" className="no-js ac-globalfooter-dark" lang="en-NG" dir="ltr" data-analytics-region="global footer" role="contentinfo" aria-labelledby="ac-gf-label">
		<div className="ac-gf-content">
			<h2 className="ac-gf-label" id="ac-gf-label">Footer</h2>
			<section className="ac-gf-sosumi" aria-label="Footnotes">
				<ul>
                    <li id="footnote-1">The idea is to create a society that is non-political and free from all racial or religious distinctions, ready for the next revolution which is that of the mind. There have been different kinds of revolution, and Africans sadly missed it. hence the creation of The Sapio Club to help connect, empower and prepare great minds for the next global revolution of the Mind.</li>
                    <li id="footnote-2">The sapio society welcomes people from every walk of life whose mind, passion and IQ is in the top 2% of the population, with the objective of enjoying each other's company, supporting community development, growing clientele, Network and participating in a wide range of social and cultural activities.</li>
				</ul>
            </section>

			<nav className="ac-gf-directory " aria-label="Directory" role="navigation">
	{/*googleoff: all*/}
	<div className="ac-gf-directory-column"><input className="ac-gf-directory-column-section-state" type="checkbox" id="ac-gf-directory-column-section-state-products" />
		<div className="ac-gf-directory-column-section">
			<label className="ac-gf-directory-column-section-label" for="ac-gf-directory-column-section-state-products">
		<h3 className="ac-gf-directory-column-section-title">Explore</h3>
	</label>
			<Link to="#ac-gf-directory-column-section-state-products" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-open">
		<span className="ac-gf-directory-column-section-anchor-label">Open Menu</span>
	</Link>
			<Link to="#" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-close">
		<span className="ac-gf-directory-column-section-anchor-label">Close Menu</span>
	</Link>
			<ul className="ac-gf-directory-column-section-list">
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/gallery" data-analytics-title="Gallery">Gallery</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/events" data-analytics-title="Event Calendar">Event Calendar</Link></li>
				<li className="ac-gf-directory-column-section-item"><a className="ac-gf-directory-column-section-link" href="https://www.sapioservice.com" data-analytics-title="Event Calendar">Sapio Service</a></li>
			</ul>
		</div>
	</div>
	<div className="ac-gf-directory-column"><input className="ac-gf-directory-column-section-state" type="checkbox" id="ac-gf-directory-column-section-state-services" />
		<div className="ac-gf-directory-column-section">
			<label className="ac-gf-directory-column-section-label" for="ac-gf-directory-column-section-state-services">
		<h3 className="ac-gf-directory-column-section-title">Benefits</h3>
	</label>
			<Link to="#ac-gf-directory-column-section-state-services" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-open">
		<span className="ac-gf-directory-column-section-anchor-label">Open Menu</span>
	</Link>
			<Link to="#" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-close">
		<span className="ac-gf-directory-column-section-anchor-label">Close Menu</span>
	</Link>
			<ul className="ac-gf-directory-column-section-list">
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/benefits" data-analytics-title="Sponsorship">Sponsorship</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/benefits" data-analytics-title="Travel and Tourism">Travel and Tourism</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/benefits" data-analytics-title="Relationship and Social Life">Relationship and Social Life</Link></li>
			</ul>
		</div>
	</div>
	<div className="ac-gf-directory-column"><input className="ac-gf-directory-column-section-state" type="checkbox" id="ac-gf-directory-column-section-state-accounts" />
		<div className="ac-gf-directory-column-section">
			<label className="ac-gf-directory-column-section-label" for="ac-gf-directory-column-section-state-accounts">
		<h3 className="ac-gf-directory-column-section-title">Account</h3>
	</label>
			<Link to="#ac-gf-directory-column-section-state-accounts" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-open">
		<span className="ac-gf-directory-column-section-anchor-label">Open Menu</span>
	</Link>
			<Link to="#" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-close">
		<span className="ac-gf-directory-column-section-anchor-label">Close Menu</span>
	</Link>
			<ul className="ac-gf-directory-column-section-list">
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/register" data-analytics-title="become a member" data-analytics-exit-link>Become a Member</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="" data-analytics-title="/register">Become Platinum Member</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/login" data-analytics-title="login" data-analytics-exit-link>Login</Link></li>
			</ul>
		</div>
	</div>
	<div className="ac-gf-directory-column"><input className="ac-gf-directory-column-section-state" type="checkbox" id="ac-gf-directory-column-section-state-about" />
		<div className="ac-gf-directory-column-section">
			<label className="ac-gf-directory-column-section-label" for="ac-gf-directory-column-section-state-about">
		<h3 className="ac-gf-directory-column-section-title">About Sapio Club</h3>
	</label>
			<Link to="#ac-gf-directory-column-section-state-about" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-open">
		<span className="ac-gf-directory-column-section-anchor-label">Open Menu</span>
	</Link>
			<Link to="#" className="ac-gf-directory-column-section-anchor ac-gf-directory-column-section-anchor-close">
		<span className="ac-gf-directory-column-section-anchor-label">Close Menu</span>
	</Link>
			<ul className="ac-gf-directory-column-section-list">
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/about" data-analytics-title="events">About</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/leadership" data-analytics-title="sapioclub leadership">Sapio Club Leadership</Link></li>
				<li className="ac-gf-directory-column-section-item"><Link className="ac-gf-directory-column-section-link" to="/honourary-members" data-analytics-title="Honourary Members" data-analytics-exit-link>Honourary Members</Link></li>
			</ul>
		</div>
	</div>
	{/*googleon: all*/}
</nav>

<section className="ac-gf-footer">
	<div className="ac-gf-footer-shop" x-ms-format-detection="none">
	</div>
	<div className="ac-gf-footer-legal">
		<div className="ac-gf-footer-legal-copyright">Copyright Â©

			2020
			Sapio Club. All rights reserved.</div>
		<div className="ac-gf-footer-legal-links">
			<Link className="ac-gf-footer-legal-link" to="/legal/" data-analytics-title="terms of use">Terms of Use</Link>
			<Link className="ac-gf-footer-legal-link" to="/ng/sitemap/" data-analytics-title="site map">Site Map</Link>
		</div>
	</div>
</section>
</div>
</footer>
      </>
    );
  }
}
