import {
    REGISTER,
    LOGIN,
    LOGOUT
  } from '../types';
  import sapioApiService from '../../api/ApiService';

  const actionRegister = data => async dispatch => {
    const res = await sapioApiService('register', data);
    dispatch({ type: REGISTER, payload: res.data });
    return res;
  };

  const actionLogin = data => async dispatch => {
    const res = await sapioApiService('login', data);
    dispatch({ type: LOGIN, payload: res.data });
    return res;
  };

  const actionLogout = data => async dispatch => {
    const res = await 'test';
    dispatch({ type: LOGOUT, payload: res.data });
    return res;
  };


  export {
    actionLogin,
    actionRegister,
    actionLogout
  };
