import React, { Component } from "react";
import Header from "../common/Header";
import Sidebar from "./Sidebar";

import { Link } from "react-router-dom";

import NumberFormat from "react-number-format";

import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

export class UserDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      my_homes: [],
      my_lands: [],
    };
  }

  componentDidMount() {
    this.getUser();
  }

  getUser() {
    let id = window.location.href.split("/")[
      window.location.href.split("/").length - 1
    ];

    axios
      .get(`${axios.defaults.baseURL}/user/${id}`, {
        headers: {
          Authorization: `Bearer ${this.state.token}`,
        },
      })
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({ user: json.data.user[0] });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }

  render() {
    return (
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="page-title">
                    <div className="row text-primary py-2 pt-4">
                      <div className="col-10">
                        <div
                          className="user-welcome"
                          style={{ overflow: "hidden" }}
                        >
                          <Link to="/admin/users-list">
                            <i className="ti-arrow-left"></i> Back to Users List
                          </Link>
                          <div className="wrapper d-flex mt-2">
                            <h4 className="d-inline-block">
                              View User
                            </h4>
                          </div>
                        </div>
                      </div>
                      <div className="text-right col-2"></div>
                    </div>
                  </div>

                  <div>
                    <div
                      data-v-22924209=""
                      class="dash-card-plan bg-white mb-4"
                    >
                      <div className="row">
                        <div className="col-md-4">
                            <img src={this.state.user.image} className="plan-img p-2" alt="" />
                        </div>
                        <div className="col-md-6">
                          <div className="p-4">
                            <h5 className="text-primary mb-1 titlecase"></h5>
                            <h6 className="text-primary mb-0 titlecase"></h6>
                            <br />
                            <div className="row mb-2">
                              <div className="col">
                                <p className="mb-1 titlecase">
                                  <b>Firstname: {this.state.user.fname}</b>
                                </p>
                              </div>
                              <div className="col">
                                <p className="mb-1 titlecase">
                                    <b>Firstname: {this.state.user.lname}</b>
                                    </p>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default UserDetails;
