import React from "react";
import { render } from "react-dom";
import { BrowserRouter, Route, Switch, withRouter } from "react-router-dom";
import axios from "axios";
import { Link } from "react-router-dom";
import swal from "sweetalert";
import $ from "jquery";
import PrivateRoute from "./PrivateRoute";

import Home from "./Home";
import Login from "./auth/Login";
import Register from "./auth/Register";

import Dashboard from "./Dashboard";

// Admin Pages

// Define App URL
// axios.defaults.baseURL = 'https://www.sapioservice.com/api/api/v1';
axios.defaults.baseURL = "http://localhost:8000/api/v1";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      user: {},
    };
  }
  _loginAdmin = (email, password) => {
    $("#login-form button")
      .attr("disabled", "disabled")
      .html(
        '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Logging In...</span>'
      );
    var formData = new FormData();
    formData.append("email", email);
    formData.append("password", password);

    axios
      .post(`${axios.defaults.baseURL}/admin-login`, formData)
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          // alert(json.data.message);
          swal("Success!", json.data.message, "success", {
            button: false,
            timer: 1500,
          });
          const { id, fname, lname, email, phone } = json.data.user;
          const { token } = json.data.token;

          let userData = {
            id,
            fname,
            lname,
            email,
            phone,
            token,
            timestamp: new Date().toString(),
          };
          let appState = {
            isLoggedIn: true,
            user: userData,
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            isLoggedIn: appState.isLoggedIn,
            user: appState.user,
            token: appState.token,
          });
          // redirect home
          this.props.history.push("/admin/dashboard");
        } else swal("Error!", json.data.message, "error");
        $("#login-form button").removeAttr("disabled").html("Login");
      })
      .catch((error) => {
        // alert(`An Error Occured! ${error}`);
        swal("Login Failed!", "" + error.response.data.message, "error", {
          button: false,
          timer: 1500,
        });
        $("#login-form button").removeAttr("disabled").html("Login");
      });
  };

  _loginUser = (email, password) => {
    $("#login-form button")
      .attr("disabled", "disabled")
      .html(
        '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Logging In...</span>'
      );
    var formData = new FormData();
    formData.append("email", email);
    formData.append("password", password);

    axios
      .post(`${axios.defaults.baseURL}/login`, formData)
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          // alert(json.data.message);
          swal("Success!", json.data.message, "success", {
            button: false,
            timer: 1500,
          });
          const { id, fname, lname, email, phone } = json.data.user;
          const { token } = json.data.token;

          let userData = {
            id,
            fname,
            lname,
            email,
            phone,
            token,
            timestamp: new Date().toString(),
          };
          let appState = {
            isLoggedIn: true,
            user: userData,
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            isLoggedIn: appState.isLoggedIn,
            user: appState.user,
            token: appState.token,
          });
          // redirect home
          this.props.history.push("/dashboard");
        } else swal("Error!", json.data.message, "error");
        $("#login-form button").removeAttr("disabled").html("Login");
      })
      .catch((error) => {
        // alert(`An Error Occured! ${error}`);
        swal("Login Failed!", "" + error.response.data.message, "error", {
          button: false,
          timer: 1500,
        });
        $("#login-form button").removeAttr("disabled").html("Login");
      });
  };

  render() {
    return (
      <Switch data="data">
        <div id="main">
          <Route exact path="/" component={Home} />

          <Route
            exact
            path="/login"
            component={(props) => (
              <Login {...props} loginUser={this._loginUser} />
            )}
          />
          {/* <Route
            path="/register"
            component={(props) => (
              <Register {...props} registerUser={this._registerUser} />
            )}
          /> */}
          <Route
            path="/register"
            component={(props) => (
              <Register {...props}/>
            )}
          />



          <PrivateRoute
            exact
            path="/dashboard"
            component={(props) => <Dashboard {...props} />}
          />

          {/* Admin Routes */}

        </div>
      </Switch>
    );
  }
}

const AppContainer = withRouter((props) => <App {...props} />);
// //console.log(store.getState())
render(
  <BrowserRouter>
    <AppContainer />
  </BrowserRouter>,

  document.getElementById("root")
);
