import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./assets/css/style.css";
import Header from "./partials/Header";
import HBook from "./assets/img/h-post.png"

import swal from 'sweetalert';
// const styles = {
//   fontFamily: "sans-serif",
//   textAlign: "center"
// };

class Home extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      books: [],
      bookfireice: [],
      singleBookName:"",
      isLoggedIn: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).isLoggedIn
        : "",
      likes: 0
    };
  }

  componentDidMount() {
    // alert(this.state.token);
    this.getBookFireIce();
    this.getBooks();
    
  }

  getBookFireIce($bookName){
    axios
      // .get(`${axios.defaults.baseURL}/external-book?name=`+$bookName
      .get(`${axios.defaults.baseURL}/external-book?name=`+"A game of thrones"
      )
      .then(response => {
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
        console.log(json.data);
          this.setState({ bookfireice: json.data });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }

  getBooks(){
    axios
      .get(`${axios.defaults.baseURL}/v1/books`
      )
      .then(response => {
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
        console.log(json.data.books);
          this.setState({ books: json.data.books });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
  }


  render() {

    return (
      <div>
      <Header/>
      <div className="vertical-book-cards">
        <div className="v-scrolling-wrapper-flexbox">
              <h1>Requirement 1</h1>
              <div className="">
                Enter Name of Book<br/>
                
              <input 
    onChange={e => this.getBookFireIce(e.target.value)} />
            <pre>
              <code>
              {JSON.stringify(this.state.bookfireice, null, 3) }
              </code>
            </pre>
          </div>
        </div>
        <br/>
        <br/>
        <br/>
        <div className="v-scrolling-wrapper-flexbox">
              <h1>Requirement 2</h1>
              <h3>All Books</h3>
              <div className="">
            <pre>
              <code>
              {JSON.stringify(this.state.books, null, 3) }
              </code>
            </pre>
          </div>
        </div>
      </div>
    </div>
    );
  }
}
export default Home;
