import React from "react";
import axios from "axios";
// import { Link } from "react-router-dom";
// import Sound from 'react-sound';

import Header from "./common/Header.js";

import Candle from "./assets/images/icons/candle_white.png";
import Rose from "./assets/images/icons/rose_white.png";
import Note from "./assets/images/icons/note_white.png";
import Candle2 from "./assets/images/icons/candle_blue.png";
import Rose2 from "./assets/images/icons/rose_blue.png";
import Note2 from "./assets/images/icons/note_blue.png";
import Pen from "./assets/images/icons/pen.png";

// import Song from "./assets/sound/beiber.mp3";

class Tribute extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      users: [],
      currentPage: 1,
      usersPerPage: 2,
      selectTribute: Candle2
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    // alert(this.state.token);
    axios
      .get(`http://localhost:8000/api/v1/users/list`,
      {
        headers: {
          'Authorization': `Bearer ${this.state.token}`
        }
      }
      )
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ users: json.data.users });
        } else alert("Login Failed!");
      })
      .catch(error => {
        console.error(`An Error Occuredd! ${error}`);
      });
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  iconSwitch(e) {
    e.preventDefault();
    this.setState(
      {selectTribute: Note2}
    );
  }

  render() {

    const { users, usersPerPage } = this.state;

    // Logic for displaying users
    // const usersList = this.state.users;
    // const indexOfLastUser = currentPage * usersPerPage;
    // const indexOfFirstUser = indexOfLastUser - usersPerPage;
    // const currentUsers = usersList.slice(indexOfFirstUser, indexOfLastUser);

    // const renderUsers = currentUsers.map((user, index) => {
    //   return <ol
    //         style={{
    //           padding: 15,
    //           border: "1px solid #cccccc",
    //           width: 250,
    //           textAlign: "left",
    //           marginBottom: 15,
    //           marginLeft: "auto",
    //           marginRight: "auto"
    //         }}
    //         key={user.id}
    //         >
    //         <p>Name: {user.fname}</p>
    //         <p>Email: {user.email}</p>
    //         <Link
    //         to={{pathname: `view/${user.id}`}}
    //         >View</Link><br/>
    //         <Link
    //         to={{pathname: `edit/${user.id}`}}
    //         >Edit</Link>
    //       </ol>;
    // });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(users.length / usersPerPage); i++) {
      pageNumbers.push(i);
    }

    // const renderPageNumbers = pageNumbers.map(number => {
    //   return (
    //     <li
    //       className="pg-link"
    //       key={number}
    //       id={number}
    //       onClick={this.handleClick}
    //     >
    //       {number}
    //     </li>
    //   );
    // });

