import React, { Component } from "react";

import Header from "./common/Header";
import { Link } from "react-router-dom";
import Footer from "./common/Footer";

import html2canvas from 'html2canvas'
import { jsPDF } from "jspdf";

import NumberFormat from "react-number-format";
import { PaystackConsumer } from "react-paystack";
import $ from "jquery";

import ScriptTag from "react-script-tag";
import AOS from "aos";

import QRCode from "qrcode.react";

import axios from "axios";
import swal from "sweetalert";

import CardIcon from "./assets/images/gold-card-image.png";
import Avatar from "./assets/img/default-user-avatar.png";

var _membership;

export class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      isLoading: true,
      planAmount: "0",
    };
    AOS.init();
  }

  export = (type, name) => {

    html2canvas(document.querySelector("#capture")).then(canvas => {
      let dataURL = canvas.toDataURL('image/png');

      if (type === 'pdf') {
        const pdf = new jsPDF({
          orientation: "landscape",
          unit: "in",
          format: [14, 10]
        });

        pdf.addImage(dataURL, 'PNG', .6, .6);
        pdf.save(`${name}.pdf`);

      } else if (type === 'png') {
        var link = document.createElement('a');
        link.download = `${name}.png`;
        link.href = dataURL;
        link.click();
      }
    });
  }

  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            user: json.data.user[0],
            isLoading: false
          });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }

  selectMembership = () => {
    this.setState({
      planAmount: document.querySelector("#membership-select").value
    });
    // console.log(this.state.planAmount);
  };

  render() {

    const configNGN = {
      // reference: (new Date()).getTime(),
      // email: "hipheckt@gmail.com",
      email: this.state.user.email,
      // plan: this.state.plan,
      amount: this.state.planAmount+"00",
      currency: "NGN",
      // publicKey: 'pk_live_68d2b6e9edacd11392f5548db9047ffb4911b39d',
      publicKey: "pk_test_a842c66ae323671a5e629e8970958560b65d3ad6",
    };

    const componentPropsNGN = {
      ...configNGN,
      text: "Sapio Sibling Membership Fee",
      onSuccess: (response) => {
        // console.log(response);

        $("#reg-button")
          .attr("disabled", "disabled")
          .html(
            '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Initiating Payment...</span>'
          );

        this.setState({
          payment_ref: response.trxref,
        });

        axios({
          method: "get",
          url: `https://api.paystack.co/transaction/verify/${this.state.payment_ref}`,
          // headers: {'Authorization': `Bearer sk_live_5299c2d2e25663425657490251d1cec7d8c206aa`, 'Content-Type': 'application/json' }
          headers: {
            Authorization: `Bearer sk_test_b921c5ae7960bc5df7b895cad6b1f37ce0b29b73`,
            "Content-Type": "application/json",
          },
        })
          .then((response) => {
            console.log(response);
            return response;
          })
          .then((json) => {

            this.setState({
              auth_code: json.data.data.authorization.authorization_code,
            });

            // swal(
            //   "Success!",
            //   this.state.auth_code,
            //   "success",
            //   {
            //     button: false,
            //     // timer: 1500,
            //   }
            // );

            var formData = new FormData();
            formData.append("email", this.state.user.email);
            formData.append("auth_code", this.state.auth_code);
            formData.append("membership", _membership.value);

            axios
              .post(`${axios.defaults.baseURL}/pay-user`, formData)
              .then((response) => {
                //console.log(response);
                return response;
              })
              .then((json) => {
                if (json.data.success) {
                  // alert(`Registration Successful!`);
                  swal("Success!", json.data.message, "success", {
                    button: false,
                    timer: 1500,
                  });
                  // redirect home
                  this.props.history.push("/dashboard");
                } else {
                  // alert(`Registration Failed!`);
                  swal("Failed", "Payment Failed", "error");
                }
              })
              .catch((error) => {
                  swal("Error!", "An Error Occured!", "error", {
                  button: false,
                  // timer: 1500
                });
              });
          });
      },
      onClose: () => null,
    };

    return (
      this.state.isLoading ? null : (
        this.state.user.app_status == 0 ? (
          <>
          <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <br />
            <br />
            <br />
            <div className="flex-copy-lockup align-text">
              <div className="copy-block col">
                <h4>
                  Hi {this.state.user.fname} {this.state.user.lname} ,<br/><br/> Your application has been received and awaiting decision from the membership selection team.
                </h4>
              </div>
              </div></section></main></body>
          </>
        ) : this.state.user.app_status == 3 ? (
          <>
          <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <br />
            <br />
            <br />
            <div className="flex-copy-lockup align-text">
              <div className="copy-block col">
                <h4>
                  Hi {this.state.user.fname} {this.state.user.lname} ,<br/><br/> Your application was rejected. You can try again in 6months!
                </h4>
              </div>
              </div></section></main></body>
          </>
        ) : this.state.user.app_status == 2 ? (
          <>
          <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <br />
            <br />
            <br />
            <div className="flex-copy-lockup align-text">
              <div className="copy-block col">
                <h4>
                  Hi {this.state.user.fname} {this.state.user.lname} ,<br/><br/> Your application was successful. Select membership type and pay your membership fee!
                </h4><br/>
                <select
                        ref={(input) => (_membership = input)}
                        name="membership"
                        id="membership-select"
                        className="form-control input-field" style={{height:"50px"}}
                        onChange={this.selectMembership}
                        >
                  <option value="">Select Membership Type</option>
                  <option value="200">Non Resident - $200/month (limited benefits)</option>
                  <option value="50000">Life membership - $50,000</option>
                  <option value="2000">Individual Membership - $2,000/yr</option>
                  <option value="3800">Spouse Membership - $3,800/yr</option>
                  <option value="5000">Diplomatic Membership(for selected and notable personnels)  $5,000/yr</option>
                  <option value="100000">Corporate Membership</option>
                </select><br/>
                <PaystackConsumer {...componentPropsNGN}>
                      {({ initializePayment }) => (
                        <button
                          type="button"
                          onClick={() =>
                            swal(
                              "You are about to make payment for your Sapio Club Membership",
                              {
                                buttons: {
                                  cancel: "No, Cancel",
                                  catch: {
                                    text: "Yes, Continue",
                                    value: "catch",
                                  },
                                },
                              }
                            ).then((value) => {
                              switch (value) {
                                case "catch":
                                  initializePayment();
                                  break;

                                default:
                              }
                            })
                          }
                          id="reg-button"
                          name="register-button"
                          className="submit-button"
                          style={{ marginTop: "32px" }}
                        >
                          Pay Membership Fee - {this.state.planAmount}
                        </button>
                      )}
                    </PaystackConsumer>
              </div>
              </div></section></main></body>
          </>
        ) :
         this.state.user.app_status == 1 ? (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          >
            <br />
            <div className="flex-copy-lockup">
              <div className="copy-block col">
                <h4>
                  Hi {this.state.user.fname} {this.state.user.lname} ,
                </h4>
              </div>
              {/* CARD */}
              {this.state.user.type == 0 ? (
                <>
                <div className="cardholder" id="capture">
                  <svg
                    className="qr"
                    data-aos="fade-in"
                    data-aos-offset="100"
                    data-aos-easing="ease-in-sine"
                    data-aos-duration="2000"
                    xmlns="http://www.w3.org/2000/svg"
                    width="484.5"
                    height="404"
                    viewBox="0 0 484.5 404"
                  >
                    <defs>
                      <linearGradient
                        id="a"
                        x1="0.5"
                        x2="0.5"
                        y2="1"
                        gradientUnits="objectBoundingBox"
                      >
                        <stop offset="0" stop-color="#ce8f26" />
                        <stop offset="0.288" stop-color="#fdf5a2" />
                        <stop offset="0.562" stop-color="#ad7118" />
                        <stop offset="0.808" stop-color="#dba752" />
                        <stop offset="1" stop-color="#aa6815" />
                      </linearGradient>
                      <filter
                        id="b"
                        x="0"
                        y="0"
                        width="310"
                        height="404"
                        filterUnits="userSpaceOnUse"
                      >
                        <feOffset dy="3" input="SourceAlpha" />
                        <feGaussianBlur stdDeviation="49.5" result="c" />
                        <feFlood flood-opacity="0.161" />
                        <feComposite operator="in" in2="c" />
                        <feComposite in="SourceGraphic" />
                      </filter>
                      <pattern
                        id="f"
                        preserveAspectRatio="none"
                        width="100%"
                        height="100%"
                        viewBox="0 0 512 471"
                      >
                        <image width="512" height="471" href={CardIcon} />
                      </pattern>
                      <pattern
                        id="g"
                        preserveAspectRatio="none"
                        width="100%"
                        height="100%"
                        viewBox="0 0 218 231"
                      >
                        <image
                          width="218"
                          height="231"
                          href={
                            this.state.user.image
                              ? this.state.user.image
                              : Avatar
                          }
                        />
                      </pattern>
                    </defs>
                    <g transform="translate(16660.5 906.5)">
                      <rect
                        width="356"
                        height="232"
                        rx="10"
                        transform="translate(-16532 -824)"
                      />
                      <path
                        d="M10,0H56a0,0,0,0,1,0,0V232a0,0,0,0,1,0,0H10A10,10,0,0,1,0,222V10A10,10,0,0,1,10,0Z"
                        transform="translate(-16532 -824)"
                        fill="url(#a)"
                      />
                      <rect
                        width="101"
                        height="101"
                        transform="translate(-16285 -701)"
                        fill="#fff"
                      />
                      <g
                        transform="matrix(1, 0, 0, 1, -16660.5, -906.5)"
                        filter="url(#b)"
                      >
                        <text
                          transform="translate(159.5 252.5) rotate(-90)"
                          font-size="11"
                          font-family="HelveticaNeue-Bold, Helvetica Neue"
                          font-weight="700"
                          letter-spacing="0.1em"
                        >
                          <tspan x="0" y="0">
                            THE SAPIO CLUB
                          </tspan>
                        </text>
