import React, { Component } from "react";
import Page from 'react-page-loading'

import Header from "../common/Header";
import Sidebar from "./Sidebar";
import { Link } from "react-router-dom";

import { Line, Bar, Chart, Doughnut } from 'react-chartjs-2';


import axios from "axios";
import swal from 'sweetalert';

const doudata = {
	labels: [
		'Red',
		'Green',
		'Yellow'
	],
	datasets: [{
		data: [300, 50, 100],
		backgroundColor: [
		'#FF6384',
		'#36A2EB',
		'#FFCE56'
		],
		hoverBackgroundColor: [
		'#FF6384',
		'#36A2EB',
		'#FFCE56'
		]
	}]
};
const linedata = {
    labels: ['January', 'February', 'March', 'April', 'May', 'June', 'July'],
    datasets: [
      {
        label: 'My First dataset',
        fill: false,
        lineTension: 0.1,
        backgroundColor: 'rgba(75,192,192,0.4)',
        borderColor: 'rgba(75,192,192,1)',
        borderCapStyle: 'butt',
        borderDash: [],
        borderDashOffset: 0.0,
        borderJoinStyle: 'miter',
        pointBorderColor: 'rgba(75,192,192,1)',
        pointBackgroundColor: '#fff',
        pointBorderWidth: 1,
        pointHoverRadius: 5,
        pointHoverBackgroundColor: 'rgba(75,192,192,1)',
        pointHoverBorderColor: 'rgba(220,220,220,1)',
        pointHoverBorderWidth: 2,
        pointRadius: 1,
        pointHitRadius: 10,
        data: [65, 59, 80, 81, 56, 55, 40]
      }
    ]
  };

export class AdminDashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
              ? JSON.parse(localStorage["appState"]).user.token
              : "",
            user: [],
    };
  }
  componentDidMount() {
    axios
            .get(`${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
            {
              headers: {
                'Authorization': `Bearer ${this.state.token}`
              }
            }
            )
            .then(response => {
              console.log(response);
              return response;
            })
            .then(json => {
              if (json.data.success) {
                this.setState({ user: json.data.user[0] });
              // } else alert("Cannot Fetch Data!");
              } else swal("Oops!", "Cannot Fetch Data!", "error");;
            })
            .catch(error => {
              console.error(`An Error Occured! ${error}`);
            });
            // alert(id)
  }
  logoutUser = () => {
    localStorage.clear();
    window.location.href = '/auth';
  };
  render() {
    return (
    <>
    <Page loader={"spin"} color={"#449400"} duration={1} size={10}>
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="row align-items-stretch">
                    <div className="mb-4 portfolio-card col-lg-12 col-12">
                      <div className="wrapper h-100 p-4 dash-card shadow-xl invest-bg col">
                        <div className="row">
                          <div className="col-12">
                            <small className="mb-2 h6 text-dark font-weight-bolder">
                              Welcome
    <span className="strong"> {this.state.user.lname || ""}</span>,
                            </small>
                            <small
                              className="ml-1"
                              style={{ marginTop: "-10px" }}
                            ></small>
                            <h3
                              style={{ marginTop: "50px", fontStyle: "italic" }}
                            >
                              Enjoy Affordable Homes and Lands
                              <br />
                              with Easy and Flexible Payments.
                            </h3>
                            <div
                              className="text-primary"
                              style={{ marginTop: "50px" }}
                            >
                              <div>
                                <Link
                                  to="/evest"
                                  className="btn btn-primary"
                                  style={{ marginRight: "10px" }}
                                >
                                  Buy a House 🔥
                                </Link>
                                <Link to="/landvest" className="btn btn-dark">
                                  Buy a Land
                                </Link>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="row">
                    <div class="mb-2 col-md-6 col-12">
                      <div
                        class="btn dash-card p-4 shadow-xl outline action-btn w-100 btn-secondary"
                        target="_self"
                      >
        <Line data={linedata} />
                      </div>
                    </div>
                    <div class="mb-2 col-md-6 col-12">
                      <div
                        class="btn dash-card p-4 shadow-xl outline action-btn w-100 btn-secondary"
                        target="_self"
                      >
                            <Doughnut data={doudata} />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Page>
    </>
    );
  }
}

export default AdminDashboard;
