import React, { Component } from "react";
import Header from "./common/Header";
import Sidebar from "./common/Sidebar";

import { Link } from "react-router-dom";

import NumberFormat from 'react-number-format';

import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

export class Plans extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
              ? JSON.parse(localStorage["appState"]).user.token
              : "",
            user: [],
      home: [],
      plan: "",
      home_id: "",
    };
  }

  componentDidMount() {
    this.getUser();
    this.getHome();

  }

  getUser(){
    axios
            .get(`${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
            {
              headers: {
                'Authorization': `Bearer ${this.state.token}`
              }
            }
            )
            .then(response => {
              console.log(response);
              return response;
            })
            .then(json => {
              if (json.data.success) {
                this.setState({ user: json.data.user[0] });
              // } else alert("Cannot Fetch Data!");
              } else swal("Oops!", "Cannot Fetch Data!", "error");;
            })
            .catch(error => {
              console.error(`An Error Occured! ${error}`);
            });
            // alert(id)
  }

  getHome(){

    var id = window.location.href.split("/")[
      window.location.href.split("/").length - 1
    ];
    axios
      .get(`${axios.defaults.baseURL}/view-home/${id}`)
      .then((response) => {
        console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            home: json.data.home,
            plan: json.data.home[0].plan_code,
            home_id: json.data.home[0].id,
          });
        } else {
          console.log(`Cannot fetch data!`);
        }
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  }


  render() {
    return (
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="page-title container">
                    <div className="row text-primary py-2 pt-4">
                      <div className="col-10">
                        <div className="user-welcome" style={{overflow:"hidden"}}>
                          <div className="wrapper d-flex">
                            <h4 className="d-inline-block">My Plans</h4>
                          </div>
                        </div>
                      </div>
                      <div className="text-right col-2"></div>
                    </div>
                  </div>
                  {/* <div data-v-22924209="" class="empty-placeholder dash-card bg-white"> You have no active plans yet </div> */}
                  <table class="table table-striped">
                    <thead>
                      <tr className="text-primary">
                        <th scope="col">Plan Name</th>
                        <th scope="col">No. of Payments</th>
                        <th scope="col">Total Payments</th>
                        <th scope="col">Target Amount</th>
                        <th scope="col">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      <tr>
                        <th scope="row">1</th>
                        <td>Mark</td>
                        <td>Otto</td>
                        <td>@mdo</td>
                        <td>@mdo</td>
                      </tr>
                    </tbody>
                  </table>
                </div>
              </div>
          </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Plans;
