import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";

import Header from "./common/Header.js";

const styles = {
  fontFamily: "sans-serif",
  textAlign: "center"
};

class Home extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      users: [],
      currentPage: 1,
      usersPerPage: 2
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    // alert(this.state.token);
    axios
      .get(`http://localhost:8000/api/v1/users/list`,
      {
        headers: {
          'Authorization': `Bearer ${this.state.token}`
        }
      }
      )
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ users: json.data.users });
        } else alert("Login Failed!");
      })
      .catch(error => {
        console.error(`An Error Occuredd! ${error}`);
      });
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  render() {

    const { users, currentPage, usersPerPage } = this.state;

    // Logic for displaying users
    const usersList = this.state.users;
    const indexOfLastUser = currentPage * usersPerPage;
    const indexOfFirstUser = indexOfLastUser - usersPerPage;
    const currentUsers = usersList.slice(indexOfFirstUser, indexOfLastUser);

    const renderUsers = currentUsers.map((user, index) => {
      return <ol
            style={{
              padding: 15,
              border: "1px solid #cccccc",
              width: 250,
              textAlign: "left",
              marginBottom: 15,
              marginLeft: "auto",
              marginRight: "auto"
            }}
            key={user.id}
            >
            <p>Name: {user.fname}</p>
            <p>Email: {user.email}</p>
            <Link
            to={{pathname: `view/${user.id}`}}
            >View</Link><br/>
            <Link
            to={{pathname: `edit/${user.id}`}}
            >Edit</Link>
          </ol>;
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(users.length / usersPerPage); i++) {
      pageNumbers.push(i);
    }

    const renderPageNumbers = pageNumbers.map(number => {
      return (
        <li
          className="pg-link"
          key={number}
          id={number}
          onClick={this.handleClick}
        >
          {number}
        </li>
      );
    });

    return (
      <div>
        <Header/>
        <div className="menu-wrapper">
          <div className="center-content">
          <ul>
          <li><a href="https://www.forevermissed.com/bryceadams/tributes" id="main_page_link">Tributes</a></li>
          <li><a href="https://www.forevermissed.com/bryceadams/lifestory">
          Life </a>
          </li>
          <li className="gallery_menu">
          <a href="https://www.forevermissed.com/bryceadams/gallery/photos">Gallery</a></li>
          <li>
          <a href="https://www.forevermissed.com/bryceadams/stories">
          Stories </a>
          </li>
        </ul>
          </div>
        </div>
      </div>
    );
  }
}
// module.exports = Home;

export default Home;
