import React, { Component } from "react";
import { Link, Redirect, Route } from "react-router-dom";

import Header from "../common/Header";
import Footer from "../common/Footer";

import ScriptTag from "react-script-tag";
import NumberFormat from "react-number-format";
import { PaystackConsumer } from "react-paystack";

import AOS from "aos";
import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

var _fname, _lname, _email, _phone, _password;

export class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      auth_code: "",
    };
  }

  render() {
    const isLoggedIn = localStorage["appState"]
      ? JSON.parse(localStorage["appState"]).isLoggedIn
      : "";

    const configNGN = {
      // reference: (new Date()).getTime(),
      email: "hipheckt@gmail.com",
      // plan: this.state.plan,
      amount: "100000" + "00",
      currency: "NGN",
      // publicKey: 'pk_live_68d2b6e9edacd11392f5548db9047ffb4911b39d',
      publicKey: "pk_test_a842c66ae323671a5e629e8970958560b65d3ad6",
    };

    const componentPropsNGN = {
      ...configNGN,
      text: "Sapio Sibling Registration",
      onSuccess: (response) => {
        // console.log(response);

        $("#reg-button")
          .attr("disabled", "disabled")
          .html(
            '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Creating Account...</span>'
          );

        this.setState({
          payment_ref: response.trxref,
        });

        axios({
          method: "get",
          url: `https://api.paystack.co/transaction/verify/${this.state.payment_ref}`,
          // headers: {'Authorization': `Bearer sk_live_5299c2d2e25663425657490251d1cec7d8c206aa`, 'Content-Type': 'application/json' }
          headers: {
            Authorization: `Bearer sk_test_b921c5ae7960bc5df7b895cad6b1f37ce0b29b73`,
            "Content-Type": "application/json",
          },
        })
          .then((response) => {
            console.log(response);
            return response;
          })
          .then((json) => {

            this.setState({
              auth_code: json.data.data.authorization.authorization_code,
            });

            // swal(
            //   "Success!",
            //   this.state.auth_code,
            //   "success",
            //   {
            //     button: false,
            //     // timer: 1500,
            //   }
            // );

            var formData = new FormData();
            formData.append("fname", _fname.value);
            formData.append("lname", _lname.value);
            formData.append("email", _email.value);
            formData.append("phone", _phone.value);
            formData.append("password", _password.value);
            formData.append("auth_code", this.state.auth_code);

            axios
              .post(`${axios.defaults.baseURL}/register`, formData)
              .then((response) => {
                //console.log(response);
                return response;
              })
              .then((json) => {
                if (json.data.success) {
                  // alert(`Registration Successful!`);
                  swal("Success!", json.data.message, "success", {
                    button: false,
                    timer: 1500,
                  });
                  const { id, fname, lname, email, phone } = json.data.user;
                  const { token } = json.data.token;
                  let userData = {
                    id,
                    fname,
                    lname,
                    email,
                    phone,
                    token,
                    timestamp: new Date().toString(),
                  };
                  let appState = {
                    isLoggedIn: true,
                    user: userData,
                  };
                  // save app state with user date in local storage
                  localStorage["appState"] = JSON.stringify(appState);
                  this.setState({
                    isLoggedIn: appState.isLoggedIn,
                    user: appState.user,
                    token: appState.token,
                  });
                  // redirect home
                  this.props.history.push("/dashboard");
                } else {
                  // alert(`Registration Failed!`);
                  swal("Failed", "Registration Failed", "error");
                  $("#reg-button").removeAttr("disabled").html("Register");
                }
              })
              .catch((error) => {
                // alert("An Error Occured!" + error);
                let res =
                  (error.response.data.message.fname || "") +
                  (error.response.data.message.lname || "") +
                  (error.response.data.message.email || "") +
                  (error.response.data.message.phone || "") +
                  (error.response.data.message.password || "");
                swal("Error!", "" + res, "error", {
                  button: false,
                  // timer: 1500
                });
                //console.log(error);
                $("#reg-button").removeAttr("disabled").html("Register");
              });
          });
      },
      onClose: () => null,
    };
    return isLoggedIn ? (
      <Redirect to={{ pathname: "/dashboard", state: { from: "/register" } }} />
    ) : (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <section
          id="section-battery"
          className="section section-battery theme-dark"
          data-anim-scroll-group="section-battery"
          data-analytics-fallback-section-engagement="name:battery"
        >
          <div
            className="copy-scroll-container"
            data-component-list="CopyMagic"
          >
            <div className="sticky-copy-lockup flex-copy-lockup intro">
              <div className="" data-copy="0">
                <p className="typography-section-eyebrow align-text default-text">
                  Become a Sapio Sibling!
                </p>
                {/* <form id="reg" onSubmit={handleRegistration} method="post"> */}
                <form>
                  <div className="row">
                    <div className="form-group col">
                      <label for="firstname" className="sr-only">
                        Firstname
                      </label>
                      <br />
                      <input
                        ref={(input) => (_fname = input)}
                        type="firstname"
                        name="firstname"
                        id="firstname"
                        className="form-control input-field"
                        placeholder="Firstname"
                      />
                    </div>
                    <div className="form-group col">
                      <label for="lastname" className="sr-only">
                        Lastname
                      </label>
                      <br />
                      <input
                        ref={(input) => (_lname = input)}
                        type="lastname"
                        name="lastname"
                        id="lastname"
                        className="form-control input-field"
                        placeholder="Lastname"
                      />
                    </div>
                  </div>
                  <div className="row">
                    <div className="form-group col">
                      <label for="email" className="sr-only">
                        Email
                      </label>
                      <br />
                      <input
                        ref={(input) => (_email = input)}
                        type="email"
                        name="email"
                        id="email"
                        className="form-control input-field"
                        placeholder="Email Address"
                      />
                    </div>
                    <div className="form-group col">
                      <label for="telephone" className="sr-only">
                        Telephone
                      </label>
                      <br />
                      <input
                        ref={(input) => (_phone = input)}
                        type="telephone"
                        name="telephone"
                        id="telephone"
                        className="form-control input-field"
                        placeholder="Telephone"
                      />
                    </div>
                  </div>
                  <div className="row">
                    <div className="form-group col">
                      <label for="password" className="sr-only">
                        Password
                      </label>
                      <br />
                      <input
                        ref={(input) => (_password = input)}
                        type="password"
                        name="password"
                        id="password"
                        className="form-control input-field"
                        placeholder="Password"
                      />
                    </div>
                    <PaystackConsumer {...componentPropsNGN}>
                      {({ initializePayment }) => (
                        <button
                          type="button"
                          onClick={() =>
                            swal(
                              "You will be required to pay a membership fee of ₦100,000",
                              {
                                buttons: {
                                  cancel: "No, Cancel",
                                  catch: {
                                    text: "Yes, Continue",
                                    value: "catch",
                                  },
                                },
                              }
                            ).then((value) => {
                              switch (value) {
                                case "catch":
                                  initializePayment();
                                  break;

                                default:
                              }
                            })
                          }
                          id="reg-button"
                          name="register-button"
                          className="submit-button"
                          style={{ marginTop: "32px" }}
                        >
                          Register Account
                        </button>
                      )}
                    </PaystackConsumer>
                  </div>
                </form>
              </div>
            </div>
          </div>
        </section>
        {/*/main*/}
        <Footer />
        {/* Template Main JS File */}
        <ScriptTag
          type="text/javascript"
          src="../assets/js/built.js"
        />
      </body>
    );
  }
}
export default Register;
