import axios from "axios";
import swal from "sweetalert";

import CardIcon from "./assets/images/gold-card-image.png";
import Avatar from "./assets/img/default-user-avatar.png";

let scan_id_raw = window.location.href.split("/")[window.location.href.split("/").length - 1];

let scan_id = localStorage["scanState"] ? JSON.parse(localStorage["scanState"]).refURL : "";


// console.log(scan_id);

// save scan_id state in local storage
let scanState = {
  refURL: scan_id_raw
};
localStorage["scanState"] = JSON.stringify(scanState);

export class Scan extends Component {
  constructor(props) {
    super(props);
    this.state = {
    //   token: localStorage["appState"]
    //     ? JSON.parse(localStorage["appState"]).user.token
    //     : "",
      user: [],
    };
    AOS.init();
  }

  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/verify-user/${scan_id}`,
        {
        //   headers: {
        //     Authorization: `Bearer ${this.state.token}`,
        //   },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            user: json.data.user[0],
          });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }

