import React, { Component } from 'react'
import axios from "axios";
import { Link } from "react-router-dom";
// import Sound from 'react-sound';
import swal from 'sweetalert';
import $ from "jquery";

import Header from './common/Header'

import Pen from "./assets/images/icons/gallery.png";

var _name, _tags;


export default class Galleri extends Component {
    constructor(props) {
        super(props);
        // let { user } = this.props.appstate;
        this.state = {
          file: null,
          tributes: [],
          currentPage: 1,
          tributesPerPage: 50
        };
        this.onChange = this.onChange.bind(this);
      }

      componentDidMount() {
        this.fetchTributes();
      }

      fetchTributes = e => {
        axios
          .get(`${axios.defaults.baseURL}/videos`)
          .then(response => {
            console.log(response);
            return response;
          })
          .then(json => {
            if (json.data.success) {
              this.setState({ tributes: json.data.tributes });
            } else alert("Fetching Tributes Failed!");
          })
          .catch(error => {
            console.error(`An Error Occured! ${error}`);
          });
      }

      handleTributePost = e => {
        e.preventDefault();

        $("#upload-button")
        .attr("disabled", "disabled")
        .html("Uploading Video...");

        var formData = new FormData();
        formData.append("name", _name.value);
        formData.append("video", this.state.file);
        formData.append("tags", _tags.value);

        console.log(formData);

      axios({
          method: 'post',
          url: `${axios.defaults.baseURL}/video`,
          data: formData,
            headers: {'Content-Type': 'multipart/form-data' }
            })
        .then(response => {
          console.log(response);
          return response;
        })
        .then(json => {
          const { id, name, video, tags } = json.data.tribute;
          let tributeData = {
            id,
            name,
            video,
            tags,
            timestamp: new Date().toString()
          };
          let appState = {
            tribute: tributeData
          };
          // save app state with user date in local storage
          localStorage["appState"] = JSON.stringify(appState);
          this.setState({
            tribute: appState.tribute,
          });

          if (json.data.success === true) {
            swal("Published!", "" + json.data.message, "success", {
              button: false,
              timer: 1500
            });
            $("#name-input").val('');
            $("#video-input").val('');
            $("#tag-input").val('');
            this.fetchTributes();
            // redirect home
            // this.props.history.push("/dashboard");
          } else {
            // alert(json.data.message);
            swal("Alert!", "" + json.data.message, "warning");

            $("#upload-button")
              .removeAttr("disabled")
              .html("Upload Video(Max. 10MB)");
          }

        })
        .catch(error => {
          // alert("An Error Occured!" + error);
          console.log(error.response.data);
          swal("Warning!", "Select Video to Upload", "warning");
        });
        $("#upload-button")
              .removeAttr("disabled")
              .html("Upload Video(Max. 10MB)");

      };

    uploadVideo = e => {
    this.photoUpload.click();
    };

    onChange = event => {
    this.setState({ imageSource: '', imageData: '', file:event.target.files[0] });
    const file = event.target.files[0];
    console.log(file);

    if (file) {
        let reader = new FileReader();
        reader.readAsDataURL(event.target.files[0]);
        reader.onload = event => {
        this.setState({
            imageSource: event.target.result,
            imageData: file
        });
        };
    }
    };

    render() {
        const { currentPage, tributesPerPage } = this.state;

        const tributesList = this.state.tributes;
        const indexOfLastTribute = currentPage * tributesPerPage;
        const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
        const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

        const renderTributes = currentTributes.map((tribute, index) => {
            return (
                <div className="gallery-videos">
                    <video src={tribute.video} controls></video>
                </div>
            );
        });

    // const { currentPage, tributesPerPage } = this.state;

    // Logic for displaying tributes
    // const tributesList = this.state.tributes;
    // const indexOfLastTribute = currentPage * tributesPerPage;
    // const indexOfFirstTribute = indexOfLastTribute - tributesPerPage;
    // const currentTributes = tributesList.slice(indexOfFirstTribute, indexOfLastTribute);

    // const renderTributes = currentTributes.map((tribute, index) => {
    // return (
    //         <img src={tribute.photo} key={tribute.id} className="gallery-image" alt=""/>
    // );
    // });
        return (
            <div>
                <Header/>
                <div className="center-canvas">
                <div className="gallery-menu"><br/>
                    <Link to="gallery" className="gallery-menu-item"><h4>Photos <img src={Pen} className="pen" alt=""/></h4></Link>
                    <Link to="videos" className="gallery-menu-item"><h4>Videos <img src={Pen} className="pen" alt=""/></h4></Link>
                </div>
                <div className="video-grid">
                    {renderTributes}
                </div>
                <div><br/><br/>
                <div className="clear"></div>
                <div className="post-tribute-card">
                <h4 className="text-white">Upload Video <img src={Pen} className="pen" alt=""/></h4><br/>
                        <div className="post-tribute-section">
                        <div className="story-entry-form">
                            <form onSubmit={this.handleTributePost} method="post" id="upload-form">
                            <div class="form-group">
                                <input ref={input => (this.photoUpload = input)} onChange= {this.onChange} name="photo" type="file" class="form-control image-input" id="video-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_name = input)} name="name"  placeholder="Please enter your name" type="text" class="form-control" id="name-input"/>
                            </div>
                            <div class="form-group">
                                <input ref={input => (_tags = input)} name="tags"  placeholder="Tag friends and family, separate with comma" type="text" class="form-control" id="tag-input"/>
                            </div>
                            <button type="submit" id="upload-button" class="btn btn-primary float-right">Upload Video(Max. 10MB)</button>
                            </form>
                        </div>
                        </div>
                    </div>
                </div>
                </div>
            </div>
        )
    }
}
