import React from "react";
import { Link, Redirect, Route } from "react-router-dom";

import Header from "../common/Header";
import Footer from "../common/Footer";

import ScriptTag from "react-script-tag";
import NumberFormat from 'react-number-format';
import { PaystackConsumer } from "react-paystack";

import AOS from "aos";
import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

const Register = ({ history, registerUser = (f) => f }) => {
  let _fname, _lname, _email, _phone, _password;

  let ref_url_raw = window.location.href;

  let ref_url = localStorage["refState"]
    ? JSON.parse(localStorage["refState"]).refURL
    : "";

  // console.log(ref_url);

  // save ref_url state in local storage
  let refState = {
    refURL: ref_url_raw,
  };
  localStorage["refState"] = JSON.stringify(refState);

  const handleRegistration = (e) => {
    e.preventDefault();

    registerUser(
      _fname.value,
      _lname.value,
      _email.value,
      _phone.value,
      _password.value,
      ref_url
    );
  };

  const isLoggedIn = localStorage["appState"]
    ? JSON.parse(localStorage["appState"]).isLoggedIn
    : "";


  const configNGN = {
    // reference: (new Date()).getTime(),
    email: this.state.user.email,
    // plan: this.state.plan,
    amount: "100000"+"00",
    currency: "NGN",
    // publicKey: 'pk_live_68d2b6e9edacd11392f5548db9047ffb4911b39d',
    publicKey: "pk_test_3a1c9194d014f2629abda6e0c3f22cb931141cf4",
  };

  const componentPropsNGN = {
    ...configNGN,
    text: "Sapio Sibling Registration",
    onSuccess: (response) => {

      // console.log(response);

      this.setState({
        payment_ref: response.trxref
      });

      axios({
          method: 'get',
          url: `https://api.paystack.co/transaction/verify/${this.state.payment_ref}`,
          // headers: {'Authorization': `Bearer sk_live_5299c2d2e25663425657490251d1cec7d8c206aa`, 'Content-Type': 'application/json' }
          headers: {'Authorization': `Bearer sk_test_2e3a16e2c572cbcfd59cbc761105f58b7a34e90d`, 'Content-Type': 'application/json' }
          })
          .then(response => {
              //console.log(response);
              return response;
          })
          .then(json => {
                this.setState({
                  auth_code: json.data.data.authorization.authorization_code
                });

                swal("Success!", "Payment Plan Activation was Successful!", "success", {
                  button: false,
                  timer: 1500,
                });

                var formData = new FormData();
                formData.append("auth_code", this.state.auth_code);
                formData.append("user_id", JSON.parse(localStorage["appState"]).user.id);

                axios({
                    method: 'post',
                    url: `${axios.defaults.baseURL}/register`,
                    data: formData,
                    headers: {'Authorization': `Bearer ${JSON.parse(localStorage["appState"]).user.token}`, 'Content-Type': 'multipart/form-data' }
                    })
                    .then(response => {
                        //console.log(response);
                        return response;
                    })
                    .then(json => {
                        if (json.data.success) {
                          // swal("Success!", "" + json.data.message, "success", {
                          //   button: false,
                          //   timer: 1500,
                          // });
                        // redirect to plans
                        this.props.history.push("/dashboard");
                        } else {
                        swal("Failed", ""+ json.data.message, "error");
                        }
                    })
                    .catch(error => {
                        // alert("An Error Occured!" + error);
                        let res = ("An Error Occured");
                        swal("Error!", "" + res, "error", {
                        button: false,
                        // timer: 1500
                        });
                        //console.log(`${formData} ${error}`);
                    });
          });
    },
    onClose: () => null,
  };

  return isLoggedIn ? (
    <Redirect to={{ pathname: "/dashboard", state: { from: "/register" } }} />
  ) : (
    <body className="page-overview  ac-gn-current-music">
      <Header />
      <section
        id="section-battery"
        className="section section-battery theme-dark"
        data-anim-scroll-group="section-battery"
        data-analytics-fallback-section-engagement="name:battery"
      >
        <div className="copy-scroll-container" data-component-list="CopyMagic">
          <div className="sticky-copy-lockup flex-copy-lockup intro">
            <div className="" data-copy="0">
              <p className="typography-section-eyebrow align-text default-text">
                Become a Sapio Sibling!
              </p>
              {/* <form id="reg" onSubmit={handleRegistration} method="post"> */}
              <form>
                <div className="row">
                  <div className="form-group col">
                    <label for="firstname" className="sr-only">
                      Firstname
                    </label>
                    <br />
                    <input
                      ref={(input) => (_fname = input)}
                      type="firstname"
                      name="firstname"
                      id="firstname"
                      className="form-control input-field"
                      placeholder="Firstname"
                    />
                  </div>
                  <div className="form-group col">
                    <label for="lastname" className="sr-only">
                      Lastname
                    </label>
                    <br />
                    <input
                      ref={(input) => (_lname = input)}
                      type="lastname"
                      name="lastname"
                      id="lastname"
                      className="form-control input-field"
                      placeholder="Lastname"
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="form-group col">
                    <label for="email" className="sr-only">
                      Email
                    </label>
                    <br />
                    <input
                      ref={(input) => (_email = input)}
                      type="email"
                      name="email"
                      id="email"
                      className="form-control input-field"
                      placeholder="Email Address"
                    />
                  </div>
                  <div className="form-group col">
                    <label for="telephone" className="sr-only">
                      Telephone
                    </label>
                    <br />
                    <input
                      ref={(input) => (_phone = input)}
                      type="telephone"
                      name="telephone"
                      id="telephone"
                      className="form-control input-field"
                      placeholder="Telephone"
                    />
                  </div>
                </div>
                <div className="row">
                  <div className="form-group col">
                    <label for="password" className="sr-only">
                      Password
                    </label>
                    <br />
                    <input
                      ref={(input) => (_password = input)}
                      type="password"
                      name="password"
                      id="password"
                      className="form-control input-field"
                      placeholder="Password"
                    />
                  </div>
                  {/* <div className="form-group col">
                    <button className="submit-button" type="submit" style={{marginTop:"32px"}}>
                      Register
                    </button>
                  </div> */}
                  <PaystackConsumer {...componentPropsNGN}>
                                  {({ initializePayment }) => (
                                      <button
                                    type="button"
                                      onClick={() =>
                                        swal(
                                          "You will be required to pay your membership fee",
                                          {
                                            buttons: {
                                              cancel: "No, Cancel",
                                              catch: {
                                                text: "Yes, Continue",
                                                value: "catch",
                                              },
                                            },
                                          }
                                        ).then((value) => {
                                          switch (value) {
                                            case "catch":
                                              initializePayment();
                                              break;

                                            default:
                                          }
                                        })
                                      }
                                      id="submit"
                                      name="register-button"
                                      className="submit-button" type="submit" style={{marginTop:"32px"}}
                                    >
                                      Start Payment of {home.type} Installment
                                    </button>)}
                                </PaystackConsumer>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
      {/*/main*/}
      <Footer />
      {/* Template Main JS File */}
      <ScriptTag
        type="text/javascript"
        src="https://www.apple.com/ac/localnav/5/scripts/ac-localnav.built.js"
      />
    </body>
  );
};
export default Register;
