import React from "react";
import { Link, Redirect, Route } from "react-router-dom";

import Header from "../common/Header";
import Footer from "../common/Footer";

import ScriptTag from "react-script-tag";
import AOS from "aos";

const Login = ({ history, loginUser = (f) => f }) => {
  let _email, _password;
  const handleLogin = (e) => {
    e.preventDefault();
    loginUser(_email.value, _password.value);
  };

  const isLoggedIn = localStorage["appState"]
    ? JSON.parse(localStorage["appState"]).isLoggedIn
    : "";

  let ref_url = localStorage["refState"]
    ? JSON.parse(localStorage["refState"]).refURL
    : "";

  // console.log(ref_url);

  return isLoggedIn ? (
    <Redirect to={{ pathname: "/dashboard", state: { from: "/login" } }} />
  ) : (
    <body className="page-overview  ac-gn-current-music">
      <Header />
      <section
        id="section-battery"
        className="section section-battery theme-dark"
        data-anim-scroll-group="section-battery"
        data-analytics-fallback-section-engagement="name:battery"
      >
        <div className="copy-scroll-container" data-component-list="CopyMagic">
          <div className="sticky-copy-lockup flex-copy-lockup intro">
            <div className="" data-copy="0">
              <p className="typography-section-eyebrow align-text default-text">
                Login!
              </p>
              <form
                id="login-form"
                action=""
                onSubmit={handleLogin}
                method="post"
              >
                <div className="form-group">
                  <label for="email" className="sr-only">
                    Email
                  </label>
                  <br />
                  <input
                    ref={(input) => (_email = input)}
                    type="email"
                    name="email"
                    id="email"
                    className="form-control input-field"
                    placeholder="Email Address"
                  />
                </div>
                <div className="form-group mb-4">
                  <label for="password" className="sr-only">
                    Password
                  </label>
                  <br />
                  <input
                    ref={(input) => (_password = input)}
                    type="password"
                    name="password"
                    id="password"
                    className="form-control input-field"
                    placeholder="Password"
                  />
                </div>
                <button
                  name="login"
                  id="email-login-btn"
                  className="submit-button"
                  type="submit"
                >
                  Login
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>
      {/*/main*/}
      <Footer />
      {/* Template Main JS File */}
      <ScriptTag
        type="text/javascript"
        src="https://www.apple.com/ac/localnav/5/scripts/ac-localnav.built.js"
      />
    </body>
  );
};
export default Login;
