import React, { Component } from "react";

import Header from "./common/Header";
import { Link } from "react-router-dom";
import AOS from "aos";

import axios from "axios";
import swal from "sweetalert";

export class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
    };
    AOS.init();
  }

  componentDidMount() {
    axios
      .get(
        `${axios.defaults.baseURL}/user/${
          JSON.parse(localStorage["appState"]).user.id
        }`,
        {
          headers: {
            Authorization: `Bearer ${this.state.token}`,
          },
        }
      )
      .then((response) => {
        //console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            user: json.data.user[0],
          });
          // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
    // alert(id)
  }
  render() {

    return (
      <body className="page-overview  ac-gn-current-music">
        <Header />
        <label id="ac-ln-curtain" for="ac-ln-menustate"></label>
        <main id="main" className="main" role="main" data-page-type="overview">
          <div className="scroll-sequence" data-component-list="ScrollSequence">
            <div className="sequence-container">
              <div className="image-sequence">
                <div className="canvas-container"></div>
              </div>
            </div>
          </div>
          <section
            id="section-hero"
            className="section section-hero theme-dark"
            data-anim-scroll-group="section-hero"
            data-analytics-fallback-section-engagement="name:hero"
            data-analytics-activitymap-region-id="hero"
          ><br/>
            <div className="flex-copy-lockup">
              <div className="copy-block col">
                  <h4>Hi {this.state.user.fname} {this.state.user.lname},</h4>
                <div className="link-container"></div>
              </div>
            </div>
          </section>
          </main>
          </body>
    );
  }
}

export default Dashboard;
