import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./assets/css/style.css";
import Header from "./partials/Header";
import HPost from "./assets/img/h-post.png"

import swal from 'sweetalert';
// const styles = {
//   fontFamily: "sans-serif",
//   textAlign: "center"
// };

class Home extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      books: [],
      isLoggedIn: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).isLoggedIn
        : "",
      currentPage: 1,
      booksPerPage: 10,
      likes: 0
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    // alert(this.state.token);
    axios
      .get(`http://localhost:8000/api/v1/books`
      )
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.status_code == 200) {
        console.log(json.data.data);
          this.setState({ books: json.data.data });
        } else alert("Cannot Fetch Books!");
      })
      .catch(error => {
        console.error(`An Error Occuredd! ${error}`);
      });
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }


  render() {


    const renderBooks = books.map((book, index) => {
      return (
          <div className="" key={book.id}>
            <span>
              <h1>{book.title}</h1>
            </span>
          </div>
      );
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(books.length / booksPerPage); i++) {
      pageNumbers.push(i);
    }

    // const renderPageNumbers = pageNumbers.map(number => {
    //   return (
    //     <li
    //       className="pg-link"
    //       key={number}
    //       id={number}
    //       onClick={this.handleClick}
    //     >
    //       {number}
    //     </li>
    //   );
    // });

    return (
      <div>
      <Header/>
      <div className="vertical-book-cards">
        <div className="v-scrolling-wrapper-flexbox">
          {renderBooks}
        </div>
      </div>
      {/* {renderPageNumbers} */}
    </div>
    );
  }
}
// module.exports = Home;

export default Home;
