import React from "react";
import { Link, Redirect, Route } from 'react-router-dom'

import Header from "./common/Header";
import Footer from "./common/Footer";

import ScriptTag from "react-script-tag";
import AOS from "aos";

const Login = ({ history, loginUser = (f) => f }) => {
  let _email, _password;
  const handleLogin = (e) => {
    e.preventDefault();
    loginUser(_email.value, _password.value);
  };

  const isLoggedIn = localStorage["appState"] ? JSON.parse(localStorage["appState"]).isLoggedIn : "";

  let ref_url = localStorage["refState"] ? JSON.parse(localStorage["refState"]).refURL : "";

  // console.log(ref_url);


  return (

    isLoggedIn ? (
      <Redirect to={{ pathname: '/dashboard', state: { from: '/login' } }} />
    ) : (
      <body className="page-overview  ac-gn-current-music">
      <Header />
      <section
        id="section-battery"
        className="section section-battery theme-dark"
        data-anim-scroll-group="section-battery"
        data-analytics-fallback-section-engagement="name:battery"
      >
        <div
          className="copy-scroll-container"
          data-component-list="CopyMagic"
        >
          <div className="sticky-copy-lockup flex-copy-lockup intro">
            <div
              className="copy-block align-text intro-headline"
              data-copy="0"
            >
              <p className="typography-section-eyebrow default-text">
                Services
              </p>
            </div>
          </div>
        </div>
      </section>
      {/*/main*/}
      <Footer />
      {/* Template Main JS File */}
      <ScriptTag
        type="text/javascript"
        src="https://www.apple.com/ac/localnav/5/scripts/ac-localnav.built.js"
      />
    </body>
    )
  );
};
export default Login;
