import React, { Component } from "react";

import axios from "axios";
import $ from "jquery";
import { Link } from "react-router-dom";
import swal from 'sweetalert';

import Header from "./common/Header";
import Sidebar from "./common/Sidebar";

var _fname, _lname, _phone, _mstatus, _address, _dob, _password, _kfullname, _kemail, _kphone, _kaddress;

export class Profile extends Component {

  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      user: [],
      imageSource: '',
      file: null
    };
    this.onChange = this.onChange.bind(this);
  }

  componentDidMount() {

    // console.log(id);

    axios
      .get(`${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
      {
        headers: {
          'Authorization': `Bearer ${this.state.token}`
        }
      }
      )
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ user: json.data.user[0] });
        // } else alert("Cannot Fetch Data!");
        } else swal("Oops!", "Cannot Fetch Data!", "error");;
      })
      .catch(error => {
        console.error(`An Error Occured! ${error}`);
      });
      // alert(id)

  };

  handleUpdate = e => {
    e.preventDefault();

    $("#update-profile")
    .attr("disabled", "disabled")
    .html(
      '<i class="fa fa-spinner fa-spin fa-1x fa-fw"></i><span class="sr-only">Loading...</span>'
    );

    var formData = new FormData();
    formData.append("fname", _fname.value);
    formData.append("lname", _lname.value);
    formData.append("phone", _phone.value);
    formData.append("image", this.state.file);
    formData.append("password", _password.value);
    formData.append("mstatus", _mstatus.value);
    formData.append("address", _address.value);
    formData.append("dob", _dob.value);
    formData.append("kfullname", _kfullname.value);
    formData.append("kemail", _kemail.value);
    formData.append("kphone", _kphone.value);
    formData.append("kaddress", _kaddress.value);

    console.log(formData);

  axios({
      method: 'post',
      url: `${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
      data: formData,
      headers: {'Authorization': `Bearer ${this.state.token}`, 'Content-Type': 'multipart/form-data' }
      })
    .then(response => {
      console.log(response);
      return response;
    })
    .then(json => {
      const { id, fname, lname, email, phone, mstatus, address, dob, kfullname, kemail, kphone, kaddress  } = json.data.user;
      const { token } = json.data.token;
      let userData = {
        id,
        fname,
        lname,
        email,
        phone,
        mstatus,
        address,
        dob,
        kfullname,
        kemail,
        kphone,
        kaddress,
        token,
        timestamp: new Date().toString()
      };
      let appState = {
        isLoggedIn: true,
        user: userData
      };
      // save app state with user date in local storage
      localStorage["appState"] = JSON.stringify(appState);
      this.setState({
        isLoggedIn: appState.isLoggedIn,
        user: appState.user,
        token: appState.token
      });

      if (json.data.success === true) {
        swal("Updated!", "" + json.data.message, "success", {
          button: false,
          timer: 1500
        });

        // redirect home
        // this.props.history.push("/dashboard");
      } else {
        // alert(json.data.message);
        swal("Alert!", "" + json.data.message, "warning");
        $("#update-profile")
          .removeAttr("disabled")
          .html("Update Profile");
      }

    })
    .catch(error => {
      // alert("An Error Occured!" + error);
      console.log(error.response.data);
      if(error.response.data.message.fname === "") {
        swal("Warning!", "" + error.response.data.message.fname, "warning");
      } else{
        swal("Warning!", "Please fill all fields", "warning");
      }
      $("#update-profile")
        .removeAttr("disabled")
        .html("Update");
    });

  };

  // onChange(e) {
  //   this.setState({file:e.target.files[0]});
  // }

  uploadPhoto = e => {
    this.photoUpload.click();
  };

  onChange = event => {
    this.setState({ imageSource: '', imageData: '', file:event.target.files[0] });
    const file = event.target.files[0];
    console.log(file);

    if (file) {
      let reader = new FileReader();
      reader.readAsDataURL(event.target.files[0]);
      reader.onload = event => {
        this.setState({
          imageSource: event.target.result,
          imageData: file
        });
      };
    }
  };


  render() {

    $("#fname-input")
    .attr("value", this.state.user.fname);
    $("#lname-input")
    .attr("value", this.state.user.lname);
    // $("#email-input")
    // .attr("value", this.state.user.email);
    $("#phone-input")
    .attr("value", this.state.user.phone);
    $("#image-input")
    .attr("value", this.state.user.password);

    return (
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div data-v-4c22b934="">
                    <div data-v-4c22b934="" id="profile-page">
                      <div className="page-title">
                        <div className="row text-primary py-2 pt-4">
                          <div className="col-10">
                            <div
                              className="user-welcome"
                              style={{ overflow: "hidden" }}
                            >
                              <div className="wrapper d-flex">
                                <h4 className="d-inline-block">My Profile</h4>
                              </div>
                            </div>
                          </div>
                          <div className="text-right col-2"></div>
                        </div>
                      </div>
                      <div className="pb-5">
                        <div className="row">
                          <div className="col-12">
                            <div className="wrapper dash-card bg-white">
                              <div className="p-4 text-center">
                                <img
                                  src="https://ui-avatars.com/api/?name=Check+Full&amp;rounded=true&amp;size=256"
                                  id="user-logo"
                                  className="shadow-xl user-avatar"
                                />
                                <p
                                  role="button"
                                  tabindex="0"
                                  style={{
                                    textDecoration: "underline",
                                    cursor: "pointer",
                                  }}
                                >
                                  {" "}
                                  Change Photo{" "}
                                </p>
                              </div>
                              <hr className="mt-0"></hr>
                              <div className="px-4">
                                <h5 className="mb-0">Personal Data</h5>
                              </div>
                              <form className="">
                                <div className="p-4">
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        First Name
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="firstname"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Last Name
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="lastname"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Telphone
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="telephone"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Marital Status
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="status"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Residential Address
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="address"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Date of Birth
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="date"
                                          type="date"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>

                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Password
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="password"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                </div>
                                <hr className="mt-0"></hr>
                                <div className="px-4">
                                  <h5 className="mb-0">Next of Kin</h5>
                                </div>
                                <div className="p-4">
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Full Name
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="fullname"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Email
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="kin_email"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Telephone
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="kin_phone"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <fieldset className="form-group">
                                    <div className="form-row">
                                      <legend
                                        tabindex="-1"
                                        className="col-sm-4 col-lg-3 bv-no-focus-ring col-form-label"
                                      >
                                        Address
                                      </legend>
                                      <div
                                        tabindex="-1"
                                        role="group"
                                        className="bv-no-focus-ring col"
                                      >
                                        <input
                                          id="kin_address"
                                          type="text"
                                          required="required"
                                          aria-required="true"
                                          className="form-control"
                                        />
                                      </div>
                                    </div>
                                  </fieldset>
                                  <div className="text-center">
                                    <button
                                      id="update-profile"
                                      type="submit"
                                      className="btn btn-primary my-4"
                                    >
                                      Update Profile
                                    </button>
                                  </div>
                                </div>
                              </form>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Profile;
