import React, { Component } from "react";
import Header from "./common/Header";
import Sidebar from "./common/Sidebar";

import { Link } from "react-router-dom";

import NumberFormat from 'react-number-format';

import axios from "axios";
import swal from "sweetalert";
import $ from "jquery";

export class Plans extends Component {
  constructor(props) {
    super(props);
    this.state = {
      token: localStorage["appState"]
              ? JSON.parse(localStorage["appState"]).user.token
              : "",
            user: [],
      my_homes: [],
    };
  }

  componentDidMount() {
    this.getUser();
    this.getMyHomes();

  }

  getUser(){
    axios
            .get(`${axios.defaults.baseURL}/user/${JSON.parse(localStorage["appState"]).user.id}`,
            {
              headers: {
                'Authorization': `Bearer ${this.state.token}`
              }
            }
            )
            .then(response => {
              console.log(response);
              return response;
            })
            .then(json => {
              if (json.data.success) {
                this.setState({ user: json.data.user[0] });
              // } else alert("Cannot Fetch Data!");
              } else swal("Oops!", "Cannot Fetch Data!", "error");;
            })
            .catch(error => {
              console.error(`An Error Occured! ${error}`);
            });
            // alert(id)
  }

  getMyHomes(){

    axios
      .get(`${axios.defaults.baseURL}/my-homes/${JSON.parse(localStorage["appState"]).user.id}`)
      .then((response) => {
        console.log(response);
        return response;
      })
      .then((json) => {
        if (json.data.success) {
          this.setState({
            my_homes: json.data.my_homes,
          });
        } else {
          console.log(`Cannot fetch data!`);
        }
      })
      .catch((error) => {
        console.error(`An Error Occured! ${error}`);
      });
  }


  render() {
    return (
      <div id="app" className="bg-gray-200">
        <div className="dashboard-page">
          <div className="private-layout">
            <div className="app-wrapper">
              <Sidebar />
              <Header />
              <div id="main-app" className="position-relative">
                <div className="container mt-4">
                  <div className="page-title container">
                    <div className="row text-primary py-2 pt-4">
                      <div className="col-10">
                        <div className="user-welcome" style={{overflow:"hidden"}}>
                          <div className="wrapper d-flex">
                            <h4 className="d-inline-block">My Plans</h4>
                          </div>
                        </div>
                      </div>
                      <div className="text-right col-2"></div>
                    </div>
                  </div>
                  { this.state.my_homes != "" ? (
                    <div>
                      {this.state.my_homes.map((my_homes, index) => (
                          <div data-v-22924209="" class="dash-card-plan bg-white mb-4">
                            <div className="row">
                            <div className="col-md-4">
                            <Link to={{pathname: `view-home/${my_homes.id}`}}>
                              <img
                                src={my_homes.image}
                                className="plan-img p-2"
                                alt=""
                              />
                              </Link>
                            </div>
                            <div className="col-md-6">
                              <div className="p-4">
                                <h5 className="text-primary mb-1 titlecase">{my_homes.name}</h5>
                                <h6 className="text-primary mb-0 titlecase"><NumberFormat value={my_homes.price} displayType={'text'} thousandSeparator={true} prefix={'₦'} /> {my_homes.type}</h6>
                                <br/>
                                <div className="row">
                                  <div className="col"><p className="mb-1 titlecase"><span style={{color:"green"}}>START DATE:</span> <br/>{my_homes.start_date}</p></div>
                                  <div className="col"><p className="mb-1 titlecase"><span style={{color:"red"}}>EXPECTED END DATE:</span> <br/>{my_homes.end_date}</p></div>
                                </div>
                                <div className="row mt-2">
                                  <div className="col"><p className="mb-1 titlecase"><span>Payments:</span> <br/>{my_homes.payments * my_homes.price }</p></div>
                                  <div className="col"><p className="mb-1 titlecase"><span>Total:</span> <br/><NumberFormat value={my_homes.total} displayType={'text'} thousandSeparator={true} prefix={'₦'} /></p></div>
                                </div>
                              </div>
                            </div>
                            </div>
                          </div>
                      ))}
                    </div>
                  ) : this.state.my_homes == "" ?
                      (
                        <div data-v-22924209="" class="empty-placeholder dash-card bg-white"> You have no active plans yet </div>
                        ) :
                        null
                  }
                </div>
              </div>
              <br/><br/>
          </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Plans;
