import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./assets/css/style.css";
import Header from "./partials/Header";
import Navigation from "./partials/Navigation";
import HPost from "./assets/img/h-post.png"

import swal from 'sweetalert';
// const styles = {
//   fontFamily: "sans-serif",
//   textAlign: "center"
// };

class Home extends React.Component {
  constructor(props) {
    super(props);
    // let { user } = this.props.appstate;
    this.state = {
      token: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).user.token
        : "",
      posts: [],
      isLoggedIn: localStorage["appState"]
        ? JSON.parse(localStorage["appState"]).isLoggedIn
        : "",
      currentPage: 1,
      postsPerPage: 10,
      likes: 0
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    // alert(this.state.token);
    axios
      .get(`http://localhost:8000/api/v1/posts`,
      {
        headers: {
          'Authorization': `Bearer ${this.state.token}`
        }
      }
      )
      .then(response => {
        console.log(response);
        return response;
      })
      .then(json => {
        if (json.data.success) {
          this.setState({ posts: json.data.posts });
        } else alert("Cannot Fetch Posts!");
      })
      .catch(error => {
        console.error(`An Error Occuredd! ${error}`);
      });
  }

  handleClick(event) {
    this.setState({
      currentPage: Number(event.target.id)
    });
  }

  likePost = () => {

    var id = this.state.posts[0].id;

    axios
    .get(`http://localhost:8000/api/v1/like/${id}`,
    {
      headers: {
        'Authorization': `Bearer ${this.state.token}`
      }
    }
    )
    .then(response => {
      console.log(response);
      return response;
    })
    .then(json => {
      if (json.data.success) {
        this.setState({ likes: json.data.like[0] });
      } else alert("Error liking post!");
    })
    .catch(error => {
      console.error(`An Error Occured! ${error}`);
    });
    swal("", "You liked this post", "success", {
        button: false,
        timer: 1500,
      });
    }

  render() {

    const { posts, currentPage, postsPerPage } = this.state;

    // Logic for displaying posts
    const postsList = this.state.posts;
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentPosts = postsList.slice(indexOfFirstPost, indexOfLastPost);

    const renderPosts = currentPosts.map((post, index) => {
      return (
          <div className="v-card" key={post.id}>
            <span className="v-card-name"><span className="v-user-avatar"><img src={post.avatar} alt=""/></span><span className="v-card-u-name">{post.fname} {post.lname}</span><span className="v-card-title">{post.post}</span></span>
            <Link to={{pathname: `view-post/${post.id}`}}> 
              <img src={post.image} alt="" className="v-card-img"/>
            </Link>
            <div className="post-action">
            <span>
                    <Link onClick={this.likePost}>
                      <span  className="action">
                        <svg className="action-icon" xmlns="http://www.w3.org/2000/svg" width="21.581" height="18.76" viewBox="0 0 21.581 18.76">
                            <g id="_17" data-name="17" transform="translate(0 0)">
                                <g id="Group_17" data-name="Group 17" transform="translate(0)">
                                <path id="Path_4" data-name="Path 4" d="M35.965,32.1l-1.16,1.16-1.16-1.16a5.632,5.632,0,0,0-7.983,0h0a5.681,5.681,0,0,0,0,7.983L34.806,49.2l9.142-9.142a5.632,5.632,0,0,0,0-7.983h0A5.656,5.656,0,0,0,35.965,32.1Z" transform="translate(-24.025 -30.437)" fill="#7a8fa6"/>
                                </g>
                            </g>
                        </svg>
                    </span> <span  className="action-text">{post.likes}</span>
                    </Link>
                </span>
                <span>
                    <Link onClick={this.likePost}>
                      <span  className="action">
                        <svg className="action-icon" id="Group_16" data-name="Group 16" xmlns="http://www.w3.org/2000/svg" width="19.376" height="18.913" viewBox="0 0 19.376 18.913">
                        <g id="Group_15" data-name="Group 15">
                            <path id="Path_3" data-name="Path 3" d="M9.688,0C4.319,0,0,3.807,0,8.492A8.205,8.205,0,0,0,3.593,15.13L3.743,18.3c.028.592.436.785.909.432l2.613-1.947a9.8,9.8,0,0,0,2.422.244c5.369,0,9.688-3.807,9.688-8.492S15.057,0,9.688,0Z" fill="#7a8fa6"/>
                        </g>
                        </svg>
                    </span> <span  className="action-text">{this.state.likes}</span>
                    </Link>
                </span>
            </div>
          </div>
      );
    });

    // Logic for displaying page numbers
    const pageNumbers = [];
    for (let i = 1; i <= Math.ceil(posts.length / postsPerPage); i++) {
      pageNumbers.push(i);
    }

    // const renderPageNumbers = pageNumbers.map(number => {
    //   return (
    //     <li
    //       className="pg-link"
    //       key={number}
    //       id={number}
    //       onClick={this.handleClick}
    //     >
    //       {number}
    //     </li>
    //   );
    // });

    return (
      <div>
      <Header/>
      <div className="horizontal-post-cards">
        <div className="h-scrolling-wrapper-flexbox">
          <div className="h-card">
            <img src={HPost} alt="" className="h-card-img"/>
            <span className="h-card-name"><span className="h-user-avatar"><img src={HPost} alt=""/></span>Afuwape Ifeoluwa</span>
          </div>
          <div className="h-card">
            <img src={HPost} alt="" className="h-card-img"/>
            <span className="h-card-name"><span className="h-user-avatar"><img src={HPost} alt=""/></span>Afuwape Ifeoluwa</span>
          </div>
          <div className="h-card">
            <img src={HPost} alt="" className="h-card-img"/>
            <span className="h-card-name"><span className="h-user-avatar"><img src={HPost} alt=""/></span>Afuwape Ifeoluwa</span>
          </div>
          <div className="h-card">
            <img src={HPost} alt="" className="h-card-img"/>
            <span className="h-card-name"><span className="h-user-avatar"><img src={HPost} alt=""/></span>Afuwape Ifeoluwa</span>
          </div>
        </div>
      </div>
      <div className="vertical-post-cards">
        <p className="feed-title"><span className="title-dot"></span>Sapio Feeds</p>
        <div className="v-scrolling-wrapper-flexbox">
          {renderPosts}
        </div>
      </div>
      {/* {renderPageNumbers} */}
      <Navigation/>
